" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Virology of Epstein-Barr virus",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?3/2/3114/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?3/2/3114/contributors\">",
"     John L Sullivan, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?3/2/3114/contributors\">",
