var title_f3_2_3118="Newborn procedure analgesia";
var content_f3_2_3118=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F75338&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F75338&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Analgesia for specific neonatal procedures",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Procedures",
"       </td>",
"       <td class=\"subtitle1\">",
"        Stepwise interventions*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tracheal aspiration&bull;",
"       </td>",
"       <td>",
"        Step 1, consider Step 4 or lidocaine via the endotracheal tube",
"       </td>",
"       <td>",
"        Perform rapidly, limit catheter insertion to the endotracheal tube only",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Heelstick&bull;",
"       </td>",
"       <td>",
"        Step 1 &amp; use mechanical lance",
"       </td>",
"       <td>",
"        Venipuncture is more efficient, less painful; Steps 2, 3, &amp; heel warming are ineffective",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Adhesive removal",
"       </td>",
"       <td>",
"        Use solvent swab, consider Step 1 or 4",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gastric tube insertion&bull;",
"       </td>",
"       <td>",
"        Step 1, consider Step 2",
"       </td>",
"       <td>",
"        Perform rapidly, use lubricant, avoid injury",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Venipuncture&bull;",
"       </td>",
"       <td>",
"        Steps 1 &amp; 2",
"       </td>",
"       <td>",
"        Requires less time &amp; less resampling than heelstick",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Arterial puncture&bull;",
"       </td>",
"       <td>",
"        Steps 1 &amp; 2, consider Step 5",
"       </td>",
"       <td>",
"        More painful than venipuncture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Intravenous cannulation&bull;",
"       </td>",
"       <td>",
"        Steps 1 &amp; 2",
"       </td>",
"       <td>",
"        Data only available for topical tetracaine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chest physiotherapy",
"       </td>",
"       <td>",
"        Gentle positioning, consider Step 4",
"       </td>",
"       <td>",
"        Avoid areas of injured or inflamed skin, drains or catheters",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Removal of intravenous catheter",
"       </td>",
"       <td>",
"        Solvent swab, consider Step 1",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Wound treatment&bull;",
"       </td>",
"       <td>",
"        Step 1, consider Steps 2,4 5, or 6 depending upon the extent of the wound",
"       </td>",
"       <td>",
"        See also \"dressing change\"",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tracheal intubation",
"       </td>",
"       <td>",
"        Step 4 or 6, use muscle relaxant only if experienced clinician, consider atropine",
"       </td>",
"       <td>",
"        Superiority of a specific drug regimen over the others has not been investigated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Central line placement",
"       </td>",
"       <td>",
"        Steps 1, 2, 5, consider Step 4 or 6",
"       </td>",
"       <td>",
"        Some centers prefer using general anesthesia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Finger stick&bull;",
"       </td>",
"       <td>",
"        Step 1 &amp; use mechanical lance",
"       </td>",
"       <td>",
"        Venipuncture is more efficient, less painful; Steps 2 &amp; 3 are ineffective",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Umbilical catheterization",
"       </td>",
"       <td>",
"        Step 1, avoid sutures on skin",
"       </td>",
"       <td>",
"        Cord tissue is not innervated, avoid injury to skin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bladder compression&bull;",
"       </td>",
"       <td>",
"        Consider Steps 1, 4",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tracheal extubation",
"       </td>",
"       <td>",
"        Use solvent swab, consider Step 1",
"       </td>",
"       <td>",
"        Step 1 may be used after the procedure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Subcutaneous injection&bull;",
"       </td>",
"       <td>",
"        Avoid if possible, Steps 1 &amp; 2",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Intramuscular injection&bull;",
"       </td>",
"       <td>",
"        Avoid if possible, Steps 1 &amp; 2",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dressing change",
"       </td>",
"       <td>",
"        Step 1, consider Steps 4 or 6 (if extensive)",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lumbar puncture",
"       </td>",
"       <td>",
"        Steps 1, 2, 5, careful positioning",
"       </td>",
"       <td>",
"        Consider Step 4 if patient is intubated/ventilated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Peripheral arterial line&bull;",
"       </td>",
"       <td>",
"        Steps 1 &amp; 2, consider Steps 5, 4",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Circumcision",
"       </td>",
"       <td>",
"        Steps 1, 2, 5, consider Step 3 post-procedure",
"       </td>",
"       <td>",
"        Step 5: Distal, ring, or dorsal penile nerve blocks; liposomal lidocaine is more effective than DPNB",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Suprapubic bladder aspiration",
"       </td>",
"       <td>",
"        Steps 1 &amp; 2, consider Steps 4 or 5",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Areterial or venous cutdown",
"       </td>",
"       <td>",
"        Steps 1, 2, 4, 5, consider Step 6",
"       </td>",
"       <td>",
"        Avoid if possible",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Peripheral insertion of central catheter (PICC line placement)",
"       </td>",
"       <td>",
"        Steps 1 &amp; 2, consider Steps 4 &amp; 5",
"       </td>",
"       <td>",
"        Some centers prefer using general anesthesia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\" colspan=\"3\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        * Stepwise interventions with increasing analgesia as the degree of anticipated procedural pain increases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"3\">",
"        <strong>",
"         Step 1:",
"        </strong>",
"        Nonpharmacologic measures: pacifier, oral sucrose, swaddling, kangaroo care (skin-to-skin contact with the mother), sensorial saturation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"3\">",
"        <strong>",
"         Step 2:",
"        </strong>",
"        Topical anesthetics (topical lidocaine, lidocaine-prilocaine cream, amethocaine gel, tetracaine gel)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"3\">",
"        <strong>",
"         Step 3:",
"        </strong>",
"        Acetaminophen: orally (10-15 mg/kg per dose every 6 to 8 hours) or rectally (20-25 mg/kg per dose every 6 to 8 hours)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"3\">",
"        <strong>",
"         Step 4:",
"        </strong>",
"        Opioids: slow intravenous infusion of fentanyl (1-2 mcg/kg per hour) or morphine (10-30 mcg/kg per hour)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"3\">",
"        <strong>",
"         Step 5:",
"        </strong>",
"        Lidocaine: subcutaneous infilitration (0.5 mL/kg of 1 percent lidocaine solution OR 0.25 mL/kg of 2 percent lidocaine solution) or as nerve block",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" colspan=\"3\">",
"        <strong>",
"         Step 6:",
"        </strong>",
"        Deep sedation/analgesia using fentanyl (2-4 mcg/kg) or morphine (50-100 mcg/kg), with midazolam (50-100 mg/kg), ketamine (1-2 mg/kg), or thiopental sodium (2 mg/kg)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     &bull; The frequency of these procedures can be reduced without sacrificing the quality of neonatal care.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: KJS Anand, MD.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
