var title_f3_20_3404="Differential diagnosis of pediatric limp etiology by gait";
var content_f3_20_3404=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F62511&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F62511&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Causes of limp by gait abnormality in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Type of limp",
"      </td>",
"      <td class=\"subtitle1\">",
"       Characteristics",
"      </td>",
"      <td class=\"subtitle1\">",
"       Etiology",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Antalgic gait",
"      </td>",
"      <td>",
"       Most common; short stance phase caused by pain in the weight-bearing extremity",
"      </td>",
"      <td>",
"       Fracture (including toddler or Salter I fracture), soft tissue injury, transient synovitis, osteomyelitis, septic arthritis, foreign body in forefoot, osteochondritis dissecans, tumor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Trendelenberg gait",
"      </td>",
"      <td>",
"       Downward pelvic tilt during the swing phase caused by weakness or spasm in the contralateral gluteus medius muscle",
"      </td>",
"      <td>",
"       Legg-Calv&eacute;-Perthes, slipped capital femoral epiphysis, developmental dysplasia of the hip",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Steppage gait",
"      </td>",
"      <td>",
"       Seen with a foot drop; presents with exaggerated hip and knee flexion during the swing phase to clear the dropped foot from the floor",
"      </td>",
"      <td>",
"       Neurologic diseases which cause loss of dorsiflexion of the ankle",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Toe-walking gait",
"      </td>",
"      <td>",
"       Child walks on his/her toes caused by heel pain or by increased flexor muscle tone in the lower leg",
"      </td>",
"      <td>",
"       Mild cerebral palsy, Sever disease, heel foreign body, idiopathic, toddler calcaneal fx",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Vaulting gait",
"      </td>",
"      <td>",
"       The knee is hyperextended and locked at the end of the stance phase of the gait and the child vaults over the extremity",
"      </td>",
"      <td>",
"       Limb length discrepancy or abnormal knee mobility",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Stooping gait",
"      </td>",
"      <td>",
"       Patient shuffles with hip flexed due to irritation of the psoas muscle by intraabdominal inflammation.",
"      </td>",
"      <td>",
"       Appendicitis, pelvic inflammatory disease, psoas muscle abscess",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    See Figure 1 for detailed description of the gait cycle.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
