var title_f3_22_3436="Differential diagnosis of upper abdominal pain";
var content_f3_22_3436=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F65698&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F65698&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Differential diagnosis of upper abdominal pain",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Diagnosis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Functional dyspepsia (up to 60 percent)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Dyspepsia caused by structural or biochemical disease",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Peptic ulcer disease",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Gastroesophageal reflux disease (GERD)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Biliary pain",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Chronic abdominal wall pain",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Gastric or esophageal cancer",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Gastroparesis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Pancreatitis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Carbohydrate malabsorption",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Medications (including potassium supplements, digitalis, iron, theophylline, oral antibiotics [especially ampillin and erythromycin], NSAIDs, corticosteroids, niacin, gemfibrozil, narcotics, colchicine, quinidine, estrogens, levodopa)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Infiltrative diseases of the stomach (eg, Crohn's disease sarcoidosis)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Metabolic disturbances (hypercalcemia, hyperkalemia)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Hepatoma",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Ischemic bowel disease",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Systemic disorders (diabetes mellitus, thyroid and parathyroid disorders, connective tissue disease)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Intestinal parasites (Giardia, Strongyloides)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Abdominal cancer, especially pancreatic cancer",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from:",
"     <ol>",
"      <li>",
"       Talley NJ, Silverstein MD, Agreus L, et al. Gastroenterology 1998; 114:582.",
"      </li>",
"      <li>",
"       Fisher RS, Parkman HP. N Engl J Med 1998; 339:1376.",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
