var title_f3_22_3437="Options for diabetes Tx";
var content_f3_22_3437=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F56876&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F56876&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Summary of glucose-lowering interventions",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Intervention",
"       </td>",
"       <td class=\"subtitle1\">",
"        Expected decrease in A1C with monotherapy, percent",
"       </td>",
"       <td class=\"subtitle1\">",
"        Advantages",
"       </td>",
"       <td class=\"subtitle1\">",
"        Disadvantages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Tier 1: well-validated core",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3_left\" colspan=\"4\">",
"        Step 1: initial therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Lifestyle to decrease weight and increase activity",
"       </td>",
"       <td>",
"        1.0 to 2.0",
"       </td>",
"       <td>",
"        Broad benefits",
"       </td>",
"       <td>",
"        Insufficient for most within first year",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Metformin",
"       </td>",
"       <td>",
"        1.0 to 2.0",
"       </td>",
"       <td>",
"        Weight neutral",
"       </td>",
"       <td>",
"        GI side effects, contraindicated with renal insufficiency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3_left\" colspan=\"4\">",
"        Step 2: additional therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Insulin",
"       </td>",
"       <td>",
"        1.5 to 3.5",
"       </td>",
"       <td>",
"        No dose limit, rapidly effective, improved lipid profile",
"       </td>",
"       <td>",
"        One to four injections daily, monitoring, weight gain, hypoglycemia, analogues are expensive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Sulfonylurea",
"       </td>",
"       <td>",
"        1.0 to 2.0",
"       </td>",
"       <td>",
"        Rapidly effective",
"       </td>",
"       <td>",
"        Weight gain, hypoglycemia (especially with glibenclamide or chlorpropamide)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Tier 2: less well validated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        TZDs",
"       </td>",
"       <td>",
"        0.5 to 1.4",
"       </td>",
"       <td>",
"        Improved lipid profile (pioglitazone), potential decrease in MI (pioglitazone)",
"       </td>",
"       <td>",
"        Fluid retention, HF, weight gain, bone fractures, expensive, potential increase in MI (rosiglitazone)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        GLP-1 agonist",
"       </td>",
"       <td>",
"        0.5 to 1.0",
"       </td>",
"       <td>",
"        Weight loss",
"       </td>",
"       <td>",
"        Requires injection, frequent GI side effects, long-term safety not established, expensive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\">",
"        Other therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        &alpha;-Glucosidase inhibitor",
"       </td>",
"       <td>",
"        0.5 to 0.8",
"       </td>",
"       <td>",
"        Weight neutral",
"       </td>",
"       <td>",
"        Frequent GI side effects, three times/day dosing, expensive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Glinide",
"       </td>",
"       <td>",
"        0.5 to 1.5*",
"       </td>",
"       <td>",
"        Rapidly effective",
"       </td>",
"       <td>",
"        Weight gain, three times/day dosing, hypoglycemia, expensive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pramlintide",
"       </td>",
"       <td>",
"        0.5 to 1.0",
"       </td>",
"       <td>",
"        Weight loss",
"       </td>",
"       <td>",
"        Three injections daily, frequent GI side effects, long-term safety not established, expensive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        DPP-4 inhibitor",
"       </td>",
"       <td>",
"        0.5 to 0.8",
"       </td>",
"       <td>",
"        Weight neutral",
"       </td>",
"       <td>",
"        Long-term safety not established, expensive",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     HF: heart failure; GI: gastrointestinal; MI: myocardial infarction.",
"     <br>",
"      * Repaglinide more effective in lowering A1C than nateglinide.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Nathan DM, Buse JB, Davidson MB, et al. Medical Management of Hyperglycemia in Type 2 Diabetes: A Consensus Algorithm for the Initiation and Adjustment of Therapy: A consensus statement of the American Diabetes Association and the European Association for the Study of Diabetes. Diabetes Care 2009; 32:193-203. Copyright &copy; 2009 American Diabetes.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f3_22_3437=[""].join("\n");
var outline_f3_22_3437=null;
var title_f3_22_3438="CDC guideline for emergency contraception";
var content_f3_22_3438=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F82516&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F82516&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classifications for emergency contraceptive pills*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Condition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Category",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clarifications/evidence/comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Personal characteristics and reproductive history",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Pregnancy",
"       </td>",
"       <td>",
"        Not applicable",
"       </td>",
"       <td>",
"        <em>",
"         Clarification:",
"        </em>",
"        Although this method is not indicated for a woman with a known or suspected pregnancy, no harm to the woman, the course of her pregnancy, or the fetus if ECPs are inadvertently used is known to exist.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Breastfeeding",
"       </td>",
"       <td>",
"        1",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Past ectopic pregnancy",
"       </td>",
"       <td>",
"        1",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"3\">",
"        History of bariatric surgery",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        a. Restrictive procedures: decrease storage capacity of the stomach (vertical banded gastroplasty, laparoscopic adjustable gastric band, laparoscopic sleeve gastrectomy)",
"       </td>",
"       <td class=\"sublist_other\">",
"        1",
"       </td>",
"       <td class=\"sublist_other\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        b. Malabsorptive procedures: decrease absorption of nutrients and calories by shortening the functional length of the small intestine (Roux-en-Y gastric bypass, biliopancreatic diversion)",
"       </td>",
"       <td class=\"sublist_other\">",
"        1",
"       </td>",
"       <td class=\"sublist_other\">",
"        <em>",
"         Comment:",
"        </em>",
"        Bariatric surgical procedures involving a malabsorptive component have the potential to decrease oral contraceptive effectiveness, perhaps further decreased by postoperative complications such as long-term diarrhea and/or vomiting. Because of these malabsorptive concerns, an emergency IUD might be more appropriate than ECPs.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Cardiovascular disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        History of severe cardiovascular complications",
"        <sup>",
"         &bull;",
"        </sup>",
"        (ischemic heart disease, cerebrovascular attack, or other thromboembolic conditions)",
"       </td>",
"       <td>",
"        2",
"       </td>",
"       <td>",
"        <em>",
"         Comment:",
"        </em>",
"        The duration of ECP use is less than that of regular use of COCs or POPs and thus would be expected to have less clinical impact.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Angina pectoris",
"       </td>",
"       <td>",
"        2",
"       </td>",
"       <td>",
"        <em>",
"         Comment:",
"        </em>",
"        The duration of ECP use is less than that of regular use of COCs or POPs and thus would be expected to have less clinical impact.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Rheumatic diseases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"3\">",
"        Rheumatoid arthritis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        a. On immunosuppressive therapy",
"       </td>",
"       <td class=\"sublist_other\">",
"        1",
"       </td>",
"       <td class=\"sublist_other\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        b. Not on immunosuppressive therapy",
"       </td>",
"       <td class=\"sublist_other\">",
"        1",
"       </td>",
"       <td class=\"sublist_other\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Neurologic conditions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Migraine",
"       </td>",
"       <td>",
"        2",
"       </td>",
"       <td>",
"        <em>",
"         Comment:",
"        </em>",
"        The duration of ECP use is less than that of regular use of COCs or POPs and thus would be expected to have less clinical impact.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Gastrointestinal conditions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Inflammatory bowel disease (ulcerative colitis, Crohn disease)",
"       </td>",
"       <td>",
"        1",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Severe liver disease",
"        <sup>",
"         &bull;",
"        </sup>",
"        (including jaundice)",
"       </td>",
"       <td>",
"        2",
"       </td>",
"       <td>",
"        <em>",
"         Comment:",
"        </em>",
"        The duration of ECP use is less than that of regular use of COCs or POPs and thus would be expected to have less clinical impact.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Solid organ transplantation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\" colspan=\"3\">",
"        Solid organ transplantation",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        a. Complicated: graft failure (acute or chronic), rejection, cardiac allograft vasculopathy",
"       </td>",
"       <td class=\"sublist_other\">",
"        1",
"       </td>",
"       <td class=\"sublist_other\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        b. Uncomplicated",
"       </td>",
"       <td class=\"sublist_other\">",
"        1",
"       </td>",
"       <td class=\"sublist_other\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Other",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Repeated ECP use",
"       </td>",
"       <td>",
"        1",
"       </td>",
"       <td>",
"        <em>",
"         Clarification:",
"        </em>",
"        Recurrent ECP use is an indication that the woman requires further counseling about other contraceptive options. Frequently repeated ECP use may be harmful for women with conditions classified as 2, 3, or 4 for CHC or POC use.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rape",
"       </td>",
"       <td>",
"        1",
"       </td>",
"       <td>",
"        <em>",
"         Comment:",
"        </em>",
"        Use of ECPs in cases of rape has no restrictions.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    1 = A condition for which there is no restriction for the use of the contraceptive method.",
"    <br>",
"     2 = A condition for which the advantages of using the method generally outweigh the theoretical or proven risks.",
"     <br>",
"      3 = A condition for which the theoretical or proven risks usually outweigh the advantages of",
"      <br>",
"       &nbsp;using the method.",
"       <br>",
"        4 = A condition that represents an unacceptable health risk if the contraceptive method is used.",
"        <div class=\"footnotes\">",
"         STI: sexually transmitted infection; HIV: human immunodeficiency virus; ECP: emergency contraceptive pill; IUD: intrauterine device; COC: combined oral contraceptive; POP: progestin-only pill; CHC: combined hormonal contraceptive; POC: progestin-only contraceptive.",
"         <br/>",
"         * ECPs do not protect against STI/HIV. If risk exists for STI/HIV (including during pregnancy or postpartum), the correct and consistent use of condoms is recommended, either alone or with another contraceptive method. Consistent and correct use of the male latex condom reduces the risk for STIs and HIV transmission.",
"         <br/>",
"         <span class=\"bullet\">",
"          &bull;",
"         </span>",
"         Condition that exposes a woman to increased risk as a result of unintended pregnancy.",
"        </div>",
"        <div class=\"reference\">",
"         Reproduced from: Centers for Disease Control and Prevention (CDC). U.S. Medical Eligibility Criteria for Contraceptive Use, 2010. MMWR Recomm Rep 2010; 59:1.",
"        </div>",
"       </br>",
"      </br>",
"     </br>",
"    </br>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
