var title_f3_23_3452="SSRI dosing adolescents";
var content_f3_23_3452=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F50818&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F50818&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Dosing of selective serotonin reuptake inhibitors (SSRIs) for adolescent depression",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Drug",
"       </td>",
"       <td class=\"subtitle1\">",
"        Starting dose",
"       </td>",
"       <td class=\"subtitle1\">",
"        Average effective dose",
"       </td>",
"       <td class=\"subtitle1\">",
"        Maximum dose",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fluoxetine* (Prozac)",
"       </td>",
"       <td>",
"        10 mg every morning&bull;",
"       </td>",
"       <td>",
"        20 mg every morning",
"       </td>",
"       <td>",
"        60 mg every morning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Citalopram (Celexa)",
"       </td>",
"       <td>",
"        10 mg every morning&bull;",
"       </td>",
"       <td>",
"        20 mg every morning",
"       </td>",
"       <td>",
"        40 mg every morning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Fluvoxamine (Luvox)",
"       </td>",
"       <td>",
"        50 mg every morning&bull;",
"       </td>",
"       <td>",
"        100 - 150 mg each day",
"       </td>",
"       <td>",
"        100 mg twice per day",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Paroxetine (Paxil)",
"       </td>",
"       <td>",
"        10 mg every morning&bull;",
"       </td>",
"       <td>",
"        20 mg every morning",
"       </td>",
"       <td>",
"        60 mg every morning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sertraline (Zoloft)",
"       </td>",
"       <td>",
"        25 mg every morning&bull;",
"       </td>",
"       <td>",
"        50 to 100 mg every morning",
"       </td>",
"       <td>",
"        150 mg every morning",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Escitalopram (Lexapro)",
"       </td>",
"       <td>",
"        5 mg every morning",
"       </td>",
"       <td>",
"        10 mg every morning",
"       </td>",
"       <td>",
"        20 mg every morning",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Fluoxetine is considered the first-line agent and is the only one of these drugs that is approved by the US Food and Drug Administration for the treatment of depression in children and adolescents.",
"     <br>",
"      &bull; If medication causes continued sedation, it is appropriate to switch dose to evening instead of morning.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Hagman, J Adolescent Health Update 2001; 13(3):1.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
