var title_f3_23_3454="IV rx IVC-related infxs";
var content_f3_23_3454=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F79387&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F79387&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Intravenous antimicrobial treatment of intravenous catheter-related bloodstream infection according to the specific pathogen isolated",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Pathogen",
"      </td>",
"      <td class=\"subtitle1\">",
"       Preferred antimicrobial agent",
"      </td>",
"      <td class=\"subtitle1\">",
"       Alternative antimicrobial agent",
"      </td>",
"      <td class=\"subtitle1\">",
"       Comment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"4\">",
"       Gram-positive cocci",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"4\">",
"       <em>",
"        Staphylococcus aureus",
"       </em>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Methicillin susceptible",
"      </td>",
"      <td>",
"       Nafcillin or oxacillin*",
"      </td>",
"      <td>",
"       Cefazolin or vancomycin",
"      </td>",
"      <td>",
"       --",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Methicillin resistant",
"      </td>",
"      <td>",
"       Vancomycin",
"      </td>",
"      <td>",
"       Daptomycin&Delta; or linezolid",
"      </td>",
"      <td>",
"       Strains of",
"       <em>",
"        S. aureus",
"       </em>",
"       with reduced susceptibility or resistance to vancomycin have been reported; strains resistant to linezolid and strains resistant to daptomycin have been reported",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"4\">",
"       Coagulase-negative staphylococci",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Methicillin susceptible",
"      </td>",
"      <td>",
"       Nafcillin or oxacillin",
"      </td>",
"      <td>",
"       First-generation cephalosporin or vancomycin",
"      </td>",
"      <td>",
"       Vancomycin has dosing advantages over nafcillin and oxacillin, but the latter are preferred because of concerns about increasing vancomycin resistance",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Methicillin resistant",
"      </td>",
"      <td>",
"       Vancomycin",
"      </td>",
"      <td>",
"       Daptomycin, linezolid, or quinupristin/dalfopristin&Delta;",
"      </td>",
"      <td>",
"       Strains resistant to linezolid have been reported",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"4\">",
"       <em>",
"        Enterococcus faecalis/Enterococcus faecium",
"       </em>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Ampicillin susceptible",
"      </td>",
"      <td>",
"       Ampicillin (or penicillin) &plusmn; gentamicin",
"      </td>",
"      <td>",
"       Vancomycin",
"      </td>",
"      <td>",
"       Vancomycin may have dosing advantages over ampicillin, but there are concerns about Vm resistance",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Ampicillin resistant, Vancomycin susceptible",
"      </td>",
"      <td>",
"       Vancomycin &plusmn; gentamicin",
"      </td>",
"      <td>",
"       Linezolid or daptomycin&Delta;",
"      </td>",
"      <td>",
"       Quinupristin/dalfopristin is not effective against",
"       <em>",
"        E. faecalis",
"       </em>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Ampicillin resistant, Vancomycin resistant",
"      </td>",
"      <td>",
"       Linezolid or daptomycin&Delta;",
"      </td>",
"      <td>",
"       Quinupristin/dalfopristin&Delta;",
"      </td>",
"      <td>",
"       Susceptibility of Vancomycin-resistant enterococci isolates varies; quinupristin/dalfopristin is not effective against",
"       <em>",
"        E. faecalis",
"       </em>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"4\">",
"       Gram-negative bacilli&bull;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\" colspan=\"4\">",
"       <em>",
"        Escherichia coli",
"       </em>",
"       and",
"       <em>",
"        Klebsiella",
"       </em>",
"       species",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       ESBL negative",
"      </td>",
"      <td>",
"       Third-generation cephalosporin (eg ceftriaxone)",
"      </td>",
"      <td>",
"       Ciprofloxacin or aztreonam",
"      </td>",
"      <td>",
"       Susceptibility of strains varies",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       ESBL positive",
"      </td>",
"      <td>",
"       Carbapenem (eg ertapenem, imipenem, meropenem or doripenem)",
"      </td>",
"      <td>",
"       Ciprofloxacin or aztreonam",
"      </td>",
"      <td>",
"       Susceptibility of strains varies",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       <em>",
"        Enterobacter species",
"       </em>",
"       and",
"       <em>",
"        Serratia marcescens",
"       </em>",
"      </td>",
"      <td>",
"       Carbapenem (eg ertapenem, imipenem or meropenem)",
"      </td>",
"      <td>",
"       Cefepime or ciprofloxacin",
"      </td>",
"      <td>",
"       Susceptibility of strains varies",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       <em>",
"        Acinetobacter",
"       </em>",
"       species",
"      </td>",
"      <td>",
"       Ampicillin/sulbactam or carbapenem (eg imipenem or meropenem)",
"      </td>",
"      <td>",
"       --",
"      </td>",
"      <td>",
"       Susceptibility of strains varies",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       <em>",
"        Stenotrophomonas maltophilia",
"       </em>",
"      </td>",
"      <td>",
"       TMP-SMZ",
"      </td>",
"      <td>",
"       Ticarcillin-clavulanate",
"      </td>",
"      <td>",
"       --",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       <em>",
"        Pseudomonas aeruginosa",
"       </em>",
"      </td>",
"      <td>",
"       Fourth-generation cephalosporin (cefepime) or carbapenem (imipenem or meropenem) or piperacillin-tazobactam, with or without aminoglycoside (tobramycin)",
"      </td>",
"      <td>",
"       --",
"      </td>",
"      <td>",
"       Susceptibility of strains varies",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       <em>",
"        Burkholderia cepacia",
"       </em>",
"      </td>",
"      <td>",
"       TMP-SMZ or carbapenem (imipenem or meropenem)",
"      </td>",
"      <td>",
"       --",
"      </td>",
"      <td>",
"       Other species, such as",
"       <em>",
"        B. acidovorans",
"       </em>",
"       and",
"       <em>",
"        B. pickieii",
"       </em>",
"       , may be susceptible to same antimicrobial agents",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_left\" colspan=\"4\">",
"       Uncommon pathogens",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       <em>",
"        Corynebacterium jeikeium",
"       </em>",
"       (group JK)",
"      </td>",
"      <td>",
"       Vancomycin",
"      </td>",
"      <td>",
"       Linezolid (based on in vitro activity)",
"      </td>",
"      <td>",
"       Check susceptibilities for other corynebacteria",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       <em>",
"        Chryseobacterium (Flavobacterium)",
"       </em>",
"       species",
"      </td>",
"      <td>",
"       Levofloxacin",
"      </td>",
"      <td>",
"       TMP-SMZ or imipenem or meropenem",
"      </td>",
"      <td>",
"       Based on in vitro activity",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       <em>",
"        Ochrobacterium anthropi",
"       </em>",
"      </td>",
"      <td>",
"       TMP-SMZ or ciprofloxacin",
"      </td>",
"      <td>",
"       Imipenem, meropenem, ertapenem or doripenem plus aminoglycoside",
"      </td>",
"      <td>",
"       --",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       <em>",
"        Malassezia furfur",
"       </em>",
"      </td>",
"      <td>",
"       Amphotericin B",
"      </td>",
"      <td>",
"       Voriconazole",
"      </td>",
"      <td>",
"       Intravenous lipids should be discontinued; some experts recommend removal of catheter",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    See",
"    <em>",
"     S. aureus",
"    </em>",
"    section of the text regarding important antibiotic management issues concerning linezolid.",
"    <br>",
"     TMP-SMX: Trimethoprim-sulfamethoxazole",
"     <br>",
"      ESBL: Extended spectrum beta lactamase",
"      <div class=\"footnotes\">",
"       * Penicillin, if the strain is susceptible.",
"       <br>",
"        &bull; Pending susceptibility results for the isolate.",
"        <br>",
"         &Delta; Pediatric experience with daptomycin and quinupristin/dalfopristin is limited",
"        </br>",
"       </br>",
"      </div>",
"      <div class=\"reference\">",
"      </div>",
"     </br>",
"    </br>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
