var title_f3_25_3485="Clin features vomiting children";
var content_f3_25_3485=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F53115&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F53115&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Possible implications of some signs and symptoms associated with vomiting in children",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tbody>",
"      <tr>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Contents of vomitus",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Undigested: achalasia",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Bilious: postampullary obstruction",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Blood or \"coffee ground\": gastritis, ulcer",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Bloody after forceful vomiting: Mallory-Weiss tear",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Malodorous: stasis with bacterial overgrowth",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Feculent: obstruction",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Force of vomiting",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Effortless, regurgitation: gastroesophageal reflux, rumination syndrome",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Projectile: pyloric stenosis, obstruction, cyclic vomiting syndrome,&nbsp;metabolic disease",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Food associations",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Triggered by cow's milk, soy, gluten: protein intolerance",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Multiple food exacerbants: eosinophilic gastroenteritis, fructose intolerance",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Periodicity of vomiting",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Paroxysmal, cyclic: cyclic vomiting syndrome, porphyria, carcinoid, pheochromocytoma, familial dysautonomia.",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Neurologic symptoms",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Headache, vertigo, visual changes: metabolic, toxin, CNS disease",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Fundoscopic evidence of increased ICP: CNS mass",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Other symptoms and signs",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Lack of nausea: CNS mass",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Esophageal pain: esophagitis",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Diarrhea: infectious enteritis",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Abdominal peristalsis: obstruction, pyloric stenosis",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Peritoneal signs: surgical abdomen, perforated appendicitis",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Jaundice: hepatobilary etiology or urinary tract infection in a neonate",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Surgical scars: obstruction secondary to adhesions",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Early morning vomiting: pregnancy, CNS mass",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Vomiting with meals: peptic ulcer disease, psychogenic",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Disproportionate hypotension, hyperkalemia: adrenal crisis",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CNS: central nervous system. ICP: intracranial pressure.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Verma R. Vomiting. In: Clinical Pediatric Gastroenterology. Altschuler SM, Liacouras CA (Eds),Churchill Livingstone, Philadephia 1998. p.105.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
