var title_f3_25_3486="Cicatricial alopecias";
var content_f3_25_3486=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F88347&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F88347&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Cicatricial (scarring) alopecias",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"5\" width=\"20%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Epidemiology",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features on scalp",
"       </td>",
"       <td class=\"subtitle1\">",
"        Non-scalp involvement",
"       </td>",
"       <td class=\"subtitle1\">",
"        Pathology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"5\">",
"        Lymphocytic primary cicatricial alopecias",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Discoid lupus erythematosus",
"       </td>",
"       <td>",
"        Onset usually in young adulthood; more common in women than men; may occur independently or in patients with SLE",
"       </td>",
"       <td>",
"        Erythematous, atrophic plaques with follicular plugging, hyperpigmentation, and/or hypopigmentation",
"       </td>",
"       <td>",
"        Face, ears, and other sites may be affected; a minority of cases are associated with systemic lupus erythematosus",
"       </td>",
"       <td>",
"        Interface dermatitis, epidermal atrophy, follicular plugging, basement membrane thickening, perifollicular superficial and deep lymphoplasmacytic infiltrate; DIF shows IgG, IgM, and C3 at the BMZ",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Lichen planopilaris",
"       </td>",
"       <td>",
"        Most common in Caucasian and East Indian women",
"       </td>",
"       <td>",
"        Areas of scarring surrounded by follicles with erythema and scale; itching and pain may be severe",
"       </td>",
"       <td>",
"        May be accompanied by cutaneous, nail, or mucous membrane lichen planus",
"       </td>",
"       <td>",
"        Lichenoid infiltrate around the isthmus and infundibulum, interface dermatitis, concentric lamellar fibroplasia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Frontal fibrosing alopecia",
"       </td>",
"       <td>",
"        Mostly post-menopausal women",
"       </td>",
"       <td>",
"        Perifollicular erythema, follicular hyperkeratosis, band-like frontal alopecia",
"       </td>",
"       <td>",
"        Loss of eyebrows common",
"       </td>",
"       <td>",
"        Lichenoid infiltrate around the isthmus and infundibulum, apoptotic keratinocytes in outer root sheath, concentric lamellar fibroplasia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Classic pseudopelade of Brocq",
"       </td>",
"       <td>",
"        Most common in middle-aged women",
"       </td>",
"       <td>",
"        Skin-colored scarred plaques resembling \"footprints in the snow\"",
"       </td>",
"       <td>",
"        N/A",
"       </td>",
"       <td>",
"        Lymphocytic infiltrate around infundibulum, concentric lamellar fibroplasia around follicles in later lesions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Central centrifugal cicatricial alopecia",
"       </td>",
"       <td>",
"        Most common in women of African origin",
"       </td>",
"       <td>",
"        Patch of scarring alopecia on crown of scalp that progresses centrifugally",
"       </td>",
"       <td>",
"        N/A",
"       </td>",
"       <td>",
"        Premature desquamation of the inner root sheath, loss of sebaceous glands, variable perifollicular chronic inflammation at isthmus and infundibulum, concentric lamellar fibroplasia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Alopecia mucinosa",
"       </td>",
"       <td>",
"        May be associated with mycosis fungoides or CTCL",
"       </td>",
"       <td>",
"        Erythematous or skin-colored indurated plaques with alopecia, follicular papules",
"       </td>",
"       <td>",
"        Eyebrow involvement may be present",
"       </td>",
"       <td>",
"        Mucin deposits in outer root sheath, eventual replacement of follicles by mucin, lymphocytic infiltrate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Keratosis folliculars spinulosa decalvans",
"       </td>",
"       <td>",
"        X-linked inheritance of mutation in the MBTPS2 gene; less commonly autosomal dominant transmission",
"       </td>",
"       <td>",
"        Noninflammatory, hyperkeratotic follicular papules and progressive hair loss",
"       </td>",
"       <td>",
"        Eyebrow and eyelash involvement may be present, photophobia",
"       </td>",
"       <td>",
"        Follicular plugging and hypergranulosis, primarily lymphocytic perifollicular infiltrate, fibrosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"5\">",
"        Neutrophilic primary cicatricial alopecias",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Dissecting folliculitis",
"       </td>",
"       <td>",
"        Most common in young black men",
"       </td>",
"       <td>",
"        Inflammatory papules, pustules, fluctuant nodules, abscesses; may also have keloidal scarring",
"       </td>",
"       <td>",
"        May be associated with hidradenitis suppurativa and acne conglobata",
"       </td>",
"       <td>",
"        Follicular occlusion, intrafollicular and perifollicular neutrophilic infiltrate; follicular perforation, abscesses and sinus tracts in later stages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Folliculitis decalvans",
"       </td>",
"       <td>",
"        Most common in young and middle aged adults, particularly men",
"       </td>",
"       <td>",
"        Patches of alopecia, erythematous follicular papules, pustules, follicular hyperkeratosis, tufted folliculitis",
"       </td>",
"       <td>",
"        N/A",
"       </td>",
"       <td>",
"        Interfollicular and perifollicular neutrophilic infiltrate; mixed infiltrate, granulomas, and fibrosis in later lesions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"5\">",
"        Mixed primary cicatricial alopecias",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Acne keloidalis nuchae",
"       </td>",
"       <td>",
"        Most common in young black men",
"       </td>",
"       <td>",
"        Dome-shaped papules and pustules on the occipital scalp and nape of the neck; keloids may develop",
"       </td>",
"       <td>",
"        N/A",
"       </td>",
"       <td>",
"        Neutrophilic or lymphocytic inflammatory infiltrate around the isthmus and infundibulum; granulomatous infiltrate may also be present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Acne necrotica",
"       </td>",
"       <td>",
"        Usually occurs in adults",
"       </td>",
"       <td>",
"        Umbilicated, pruritic, painful papules that undergo central necrosis and resolve with varioliform scars",
"       </td>",
"       <td>",
"        N/A",
"       </td>",
"       <td>",
"        Infundibular folliculitis with necrosis, lymphocytic or mixed inflammatory infiltrate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Erosive pustular dermatosis of the scalp",
"       </td>",
"       <td>",
"        Most common in the elderly, particularly women",
"       </td>",
"       <td>",
"        Pustules, erosions, and crusted plaques",
"       </td>",
"       <td>",
"        N/A",
"       </td>",
"       <td>",
"        Nonspecific early findings; older lesions with a chronic mixed inflammatory infiltrate and fibrosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"5\">",
"        Other",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Tinea capitis",
"       </td>",
"       <td>",
"        Usually affects children",
"       </td>",
"       <td>",
"        Scaly patches or plaques; kerion may develop",
"       </td>",
"       <td>",
"        Tinea corporis may be present",
"       </td>",
"       <td>",
"        Perifollicular inflammation and fungal elements within or around the hair shaft",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CTCL: cutaneous T-cell lymphoma; SLE: systemic lupus erythematosus; DIF: direct immunofluorescence; BMZ: basement membrane zone; SLE: systemic lupus erythematosus.",
"    </div>",
"    <div class=\"reference\">",
"     Data from:",
"     <br/>",
"     <ol>",
"      <li>",
"       Otberg N, Shapiro J. Hair growth disorders. In: Fitzpatrick's Dermatology in General Medicine, 8th ed, Goldsmith LA, Katz SI, Gilchrest BA, et al. (Eds), McGraw Hill 2012.",
"      </li>",
"      <li>",
"       Childs JM, Sperling LC. Histopathology of scarring and nonscarring hair loss. Dermatol Clin 2013; 31:43.",
"      </li>",
"     </ol>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
