" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Significance of minor red blood cell antibodies during pregnancy",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?3/27/3513/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?3/27/3513/contributors\">",
"     Vanessa A Barss, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?3/27/3513/contributors\">",
"     Kenneth J Moise Jr, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?3/27/3513/contributors\">",
