var title_f3_29_3549="Performance status measure";
var content_f3_29_3549=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F57945&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F57945&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Comparison of Karnofsky and Eastern Cooperative Oncology Group (ECOG) performance status measures",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1\" colspan=\"2\">",
"            Karnofsky",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2\">",
"            Score",
"           </td>",
"           <td class=\"subtitle2\">",
"            Definition",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"centered\">",
"            100",
"           </td>",
"           <td>",
"            Normal, no complaints, no evidence of disease",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"centered\">",
"            90",
"           </td>",
"           <td>",
"            Able to carry on normal activity, minor signs or symptoms of disease",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"centered\">",
"            80",
"           </td>",
"           <td>",
"            Normal activity with effort, some signs or symptoms of disease",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"centered\">",
"            70",
"           </td>",
"           <td>",
"            Cares for self, unable to carry on normal activity or to do active work",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"centered\">",
"            60",
"           </td>",
"           <td>",
"            Requires occasional assistance, but is able to care for most needs",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"centered\">",
"            50",
"           </td>",
"           <td>",
"            Requires considerable assistance and frequent medical care",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"centered\">",
"            40",
"           </td>",
"           <td>",
"            Disabled, requires special care and assistance",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"centered\">",
"            30",
"           </td>",
"           <td>",
"            Severely disabled, hospitalization is indicated although death is not imminent",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"centered\">",
"            20",
"           </td>",
"           <td>",
"            Hospitalization is necessary, very sick, active supportive treatment necessary",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"centered\">",
"            10",
"           </td>",
"           <td>",
"            Moribund, fatal processes progressing rapidly",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"centered\">",
"            0",
"           </td>",
"           <td>",
"            Dead",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1\" colspan=\"2\">",
"            ECOG",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"subtitle2\">",
"            Score",
"           </td>",
"           <td class=\"subtitle2\">",
"            Definition",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"centered\">",
"            0",
"           </td>",
"           <td>",
"            Fully active; no performance restrictions",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"centered\">",
"            1",
"           </td>",
"           <td>",
"            Strenuous physical activity restricted; fully ambulatory and able to carry out light work",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"centered\">",
"            2",
"           </td>",
"           <td>",
"            Capable of all selfcare but unable to carry out any work activities. Up and about &gt;50 percent of waking hours.",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"centered\">",
"            3",
"           </td>",
"           <td>",
"            Capable of only limited selfcare; confined to bed or chair &gt;50 percent of waking hours",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"centered\">",
"            4",
"           </td>",
"           <td>",
"            Completely disabled; cannot carry out any selfcare; totally confined to bed or chair",
"           </td>",
"          </tr>",
"          <tr>",
"           <td class=\"centered\">",
"            5",
"           </td>",
"           <td>",
"            Dead",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
