var title_f3_3_3133="Classification of tremors";
var content_f3_3_3133=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F69074&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F69074&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of tremors",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Rest tremors",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Parkinson disease",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Other parkinsonian syndromes (less commonly)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Midbrain (\"rubral\") tremor: Rest &lt; Postural &lt; Kinetic",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Wilson's disease (also acquired hepatocerebral degeneration)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Essential tremor - only if severe: Rest &lt; &lt; Postural &amp; Action",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Postural and action (\"terminal\") tremors",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Physiological tremor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Exaggerated physiological tremor (these factors can also aggravate other forms of tremor)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Stress, fatigue, anxiety, emotion",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Endocrine: Hypoglycemia, thyrotoxicosis, pheochromocytoma, adrenocorticosteriods",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Drugs and toxins: Beta antagonists, dopamine agonists, amphetamines, lithium, tricyclic antidepressants, neuroleptics, theophylline, caffeine, valproic acid, alcohol withdrawal, mercury (\"Hatter's shakes\"), lead, arsenic, others",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Essential tremor (familiar or sporadic)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Primary writing tremor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       With other CNS disorders",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Parkinson disease",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Other akinetic-rigid syndromes",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Idiopathic dystonia, including focal dystonias",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       With peripheral neuropathy",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Charcot-Marie-Tooth (controversial as to whether to call this the Roussy-Levy syndrome)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Variety of other peripheral neuropathies (especially dysgammaglobulinemia)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Cerebellar tremor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Kinetic (intention) tremor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td colspan=\"1\">",
"       Disease of cerebellar \"outflow\" (dentate nucleus and superior cerebellar peduncle): MS, trauma, tumour, vascular, Wilson's, acquired hepatocerebral degeneration, drugs, toxins (eg, mercury), others",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Miscellaneous rhythmical movement disorders",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Psychogenic tremor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Orthostatic tremor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Rhythmical movements in dystonia (dystonic tremor)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Rhythmical myoclonus (segmental myoclonus - eg, palatal or branchial myoclonus, spinal myoclonus, limb myorhythmia)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Oscillatory myoclonus",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Asterixis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Clonus",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Epilepsia partialis continua",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Hereditary chin quivering",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Spasmus nutans",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Head bobbing with 3rd ventricular cysts",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Nystagmus",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Jankovic, J, Lang, AE. Classification of movement disorders. In: Germano, IM, (Ed), Surgical Treatment of Movement Disorders, The American Association of Neurological Surgeons (AANS), Lebanon, NH, 1998. p. 3.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
