var title_f3_30_3563="Avoiding lead in soil PI";
var content_f3_30_3563=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PI%2F61589&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PI%2F61589&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Ways to prevent exposure to lead in soil",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Cover bare soil with grass, plants, gravel, or wood chips",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Do not let children play near walls of house or garage on bare soil",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Have children play in grassy area or sandbox that can be covered",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Wash children's hands after they play outside or play with pets",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Use a doormat to reduce track-in of outdoor dust and soil",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Environmental Protection Agency. Additional information available at:",
"     <a href=\"file://www.epa.gov/lead/\" target=\"_blank\">",
"      file://www.epa.gov/lead/",
"     </a>",
"     .",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
