" <div id=\"innerOutline\">",
"  <h1>",
"   TOPIC OUTLINE",
"  </h1>",
"  <div id=\"outline\">",
"   <ul>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#F133045\">",
"      Brand Names: Canada",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#F10023290\">",
"      Warning",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#F10023292\">",
"      What is this drug used for?",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#F10023291\">",
