" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Effect of cardiac drugs on platelet function",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?3/32/3594/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?3/32/3594/contributors\">",
"     Robert T Eberhardt, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?3/32/3594/contributors\">",
"     Joseph Loscalzo, MD, PhD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?3/32/3594/contributors\">",
