var title_f3_33_3614="HCW prophylaxis recs I";
var content_f3_33_3614=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F82359%7EID%2F60692&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F82359%7EID%2F60692&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Postexposure prophylaxis for vaccine preventable diseases in healthcare workers - I",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" colspan=\"1\" rowspan=\"2\">",
"        Disease",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"2\" rowspan=\"1\">",
"        Definition of",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"1\" rowspan=\"2\">",
"        Prophylaxis",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"1\" rowspan=\"2\">",
"        Work restrictions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\">",
"        Susceptible",
"       </td>",
"       <td class=\"subtitle2\">",
"        Exposed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Hepatitis B",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        Never or incompletely vaccinated and never had an anti-HBs titer &gt;10 IU/mL",
"       </td>",
"       <td>",
"        <p>",
"         Contact with contaminated blood or body fluid via percutaneous, membrane or non-intact skin exposure;",
"        </p>",
"        <p>",
"         <strong>",
"          Source",
"         </strong>",
"         - known HBsAg positive",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Never vaccinated",
"         </strong>",
"         - HBIG 0.06 mL/kg x1 and initiate vaccine series",
"        </p>",
"        <p>",
"         <strong>",
"          Nonresponder",
"         </strong>",
"         - HBIG x2 Response unknown - test healthcare worker; HBIG x1 if not protected and boost or complete vaccination",
"        </p>",
"       </td>",
"       <td colspan=\"1\" rowspan=\"3\">",
"        <p>",
"         <strong>",
"          Acute infection",
"         </strong>",
"         - relieve from direct patient contact until jaundice resolves;",
"        </p>",
"        <p>",
"         <strong>",
"          Chronic infection",
"         </strong>",
"         - counsel healthcare worker on infection control practices - if transmission occurs, restrict from invasive procedures and direct contact with patient equipment until hepatitis B antigenemia resolves",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         Contact with contaminated blood or body fluid via percutaneous, membrane or non-intact skin exposure;",
"        </p>",
"        <p>",
"         <strong>",
"          Source",
"         </strong>",
"         - known HBsAg negative",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Never vaccinated",
"         </strong>",
"         - Initiate vaccine series",
"        </p>",
"        <p>",
"         <strong>",
"          Nonresponder or response unknown",
"         </strong>",
"         - no treatment needed",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         Contact with contaminated blood or body fluid via percutaneous, membrane or non-intact skin exposure;",
"        </p>",
"        <p>",
"         <strong>",
"          Source",
"         </strong>",
"         - unknown HBsAg",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Never vaccinated",
"         </strong>",
"         - initiate vaccine series",
"        </p>",
"        <p>",
"         <strong>",
"          Nonresponder",
"         </strong>",
"         - treat as HBsAg positive if high risk source",
"        </p>",
"        <p>",
"         <strong>",
"          Response unknown",
"         </strong>",
"         - test healthcare worker; if not protected complete vaccination or begin revaccination.",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Influenza",
"       </td>",
"       <td>",
"        Did not recieve annual immunization",
"       </td>",
"       <td>",
"        Cohabiting confined air space or face to face contact in open area",
"       </td>",
"       <td>",
"        Catch-up immunization and consider amantidine or rimantidine 100 mg PO BID or oseltamivir 75 mg PO QD or BID",
"       </td>",
"       <td>",
"        Relieve from care of high risk patients",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Measles",
"       </td>",
"       <td>",
"        None of the following - documentation of diagnosis of measles by a physician, serologic evidence of immunity, two doses of MMR",
"       </td>",
"       <td>",
"        Cohabiting confined air space or face to face in open area",
"       </td>",
"       <td>",
"        Measles vaccine if no contraindications or immune globulin 0.25 mL/kg (maximum 15 mL) within 6 days or exposure",
"       </td>",
"       <td>",
"        Furlough from health-care facility until 4 days after rash appears or from day 5 after first exposure to day 21 after last exposure",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Postexposure prophylaxis for vaccine-preventable diseases in health care workers - II",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        Disease",
"       </td>",
"       <td class=\"subtitle1\" colspan=\"2\">",
"        Definition of",
"       </td>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        Prophylaxis",
"       </td>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        Work restrictions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\">",
"        Susceptible",
"       </td>",
"       <td class=\"subtitle2\">",
"        Exposed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Varicella zoster",
"       </td>",
"       <td>",
"        None of the following: history of varicella, serologic evidence of immunity, two doses of varicella vaccine",
"       </td>",
"       <td>",
"        Cohabiting confined air space or face-to-face contact in open area with a patient who has active lesions or in the 48 hours before onset of primary infection",
"       </td>",
"       <td>",
"        Varicella zoster immune globulin (125 U/10 kg intramuscularly) for immunocompromised or pregnant health care workers or varicella vaccine if no contraindications",
"       </td>",
"       <td>",
"        Furlough from health care facility until all lesions are dry and crusted or from day 8 after first exposure to day 21 after last exposure (extend to day 28 if VZIG given)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hepatitis A",
"       </td>",
"       <td>",
"        None of the following: history of hepatitis A, serologic evidence of immunity, two doses of hepatitis A vaccine",
"       </td>",
"       <td>",
"        Ingestion of contaminated food, contact with feces from a patient infected with hepatitis A",
"       </td>",
"       <td>",
"        Immune globulin 0.02 mL/kg&nbsp;intramuscularly in gluteal or deltoid within 14 days of exposure; consider concurrent hepatitis A vaccination",
"       </td>",
"       <td>",
"        Relieve from direct patient contact and food handling until&nbsp;seven days after onset of jaundice",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pertussis",
"       </td>",
"       <td>",
"        All health care workers",
"       </td>",
"       <td>",
"        Direct contact with respiratory secretions or droplets from infected person",
"       </td>",
"       <td>",
"        <p>",
"         Azithromycin&nbsp;500 mg&nbsp;orally&nbsp;on day one followed by 250 mg&nbsp;orally&nbsp;daily&nbsp;on days two to five (trimethoprim-sulfamethoxazole is an alternative)",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Active disease or symptomatic postexposure",
"         </strong>",
"         - furlough from health care facility from beginning of catarrhal stage to&nbsp;third week after onset of paroxysms or&nbsp;five days after effective therapy",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          Asymptomatic postexposure",
"         </strong>",
"         - no restriction providing antibiotic therapy is taken",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Neisseria meningitidis",
"       </td>",
"       <td>",
"        All health care workers",
"       </td>",
"       <td>",
"        Direct contact with respiratory secretions from infected person",
"       </td>",
"       <td>",
"        Ciprofloxacin 500 mg&nbsp;orally x 1 or ceftriaxone 250 mg&nbsp;intramuscularly x 1 or rifampin 600 mg&nbsp;orally&nbsp;twice daily&nbsp;for&nbsp;two&nbsp;days",
"       </td>",
"       <td>",
"        No restriction",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
