var title_f3_37_3675="Revised Amsterdam criteria";
var content_f3_37_3675=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F59832&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F59832&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Revised Amsterdam criteria by the International Collaborative Group on HNPCC",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        There should be at least three relatives with an HNPCC-associated cancer (colorectal cancer, cancer of the endometrium, small bowel, ureter, or renal pelvis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        One should be a first degree relative of the other two",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        At least two successive generations should be affected",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        At least&nbsp;one should be diagnosed before age 50",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Familial adenomatous polyposis should be excluded in the colorectal cancer case(s) if any",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tumors should be verified by pathological examination",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     HNPCC: hereditary nonpolyposis colorectal cancer.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from Vasen HF, Watson P, Mecklin JP, et al. Gastroenterology 1999; 116:1453.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
