" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Induction immunosuppression following lung transplantation",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?3/38/3688/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?3/38/3688/contributors\">",
"     Pamela J McShane, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?3/38/3688/contributors\">",
"     Sangeeta Bhorade, MD",
"    </a>",
"    <br/>",
"   </div>",
