" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 533px\">",
"   <div class=\"ttl\">",
"    Sleeper stretch",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 513px; height: 200px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
"   <div class=\"lgnd\">",
