var title_f3_41_3741="Systemic sclerosis";
var content_f3_41_3741=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F80559&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F80559&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of systemic sclerosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Limited cutaneous scleroderma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Raynaud phenomenon for years, occasionally decades",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Skin involvement limited to hands, face, feet, and forearms (acral distribution)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <span style=\"line-height: 115%; font-family: calibri,sans-serif; color: #000000; font-size: 11pt;\">",
"         Nailfold capillary pattern typical of scleroderma predominantly nailfold capillary loops with capillary drop-out",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        A significant (10 to 15 percent) late incidence of pulmonary hypertension, with or without skin calcification, gastrointestinal disease, telangiectasias (CREST syndrome), or interstitial lung disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Renal disease rarely occurs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <span style=\"line-height: 115%; font-family: calibri,sans-serif; color: #000000; font-size: 11pt;\">",
"         Anticentromere antibody (ACA) in 50 to 60 percent but other patterns also occurring in 5 to 10 percent&nbsp;(especially anti-PM-Scl and anti-Scl-70)",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Diffuse cutaneous scleroderma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Raynaud phenomenon followed, within one year, by puffy or hidebound skin changes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Truncal and acral skin involvement; tendon friction rubs",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <span style=\"line-height: 115%; font-family: calibri,sans-serif; color: #000000; font-size: 11pt;\">",
"         Nailfold capillary pattern typical of scleroderma with dilatation (early), dilatation and drop-out (active), and tortuosity with drop-out (late)",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Early and significant incidence of renal, interstitial lung, diffuse gastrointestinal, and myocardial disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Anti-Scl-70 (30 percent) and anti-RNA polymerase-I, II, or III (12 to 15 percent) antibodies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Scleroderma sine scleroderma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Presentation with pulmonary fibrosis or renal, cardiac, or gastrointestinal disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        No skin involvement",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Raynaud phenomenon may be present",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Antinuclear antibodies may be present (anti-Scl-70, ACA, or anti-RNA polymerase-I, II, or III)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Environmentally induced scleroderma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Generally diffuse distribution of skin sclerosis and a history of exposure to an environmental agent suspected of causing scleroderma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Overlap syndromes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Features of systemic sclerosis&nbsp;that coexist with those of another autoimmune rheumatic disease such as systemic lupus erythematosus, rheumatoid arthritis, dermatomyositis, vasculitis, or Sj&ouml;gren's syndrome.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Pre-scleroderma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Raynaud phenomenon",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <span style=\"line-height: 115%; font-family: calibri,sans-serif; color: #000000; font-size: 11pt;\">",
"         Nailfold capillary changes (early or active pattern typical) and evidence of digital ischemia",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <span style=\"line-height: 115%; font-family: calibri,sans-serif; color: #000000; font-size: 11pt;\">",
"         Specific circulating autoantibodies - anti-topoisomerase-I (Scl-70), anti-centromere (ACA), or anti-RNA polymerase-I, II, or III or other hallmark scleroderma reactivity",
"        </span>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
