var title_f3_42_3756="WHO discharge criteria";
var content_f3_42_3756=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F79197&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F79197&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Criteria for discharge from non-residential care",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Child",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Weight-for-height has reached -1 SD (90 percent) of NCHS/WHO median reference values",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Eating an adequate amount of a nutritious diet that the mother can prepare at home",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Gaining weight at a normal or increased rate (about 10 to 15 g/kg per day)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        All vitamin and mineral deficiencies have been treated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        All infections and other conditions have been or are being treated, including anaemia, diarrhoea, intestinal parasitic infections, malaria, tuberculosis and otitis media",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Full immunization programme started",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Mother or carer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Able and willing to look after the child",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Knows how to prepare appropriate foods and to feed the child",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Knows how to make appropriate toys and to play with the child",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Knows how to give home treatment for diarrhoea, fever and acute respiratory infections, and how to recognize the signs that mean she must seek medical assistance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Health worker",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Able to ensure follow-up of the child and support for the mother",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Management of Severe Malnutrition: A Manual for Physicians and other Senior Health Workers. World Health Organization, Geneva, 1999. Copyright &copy; 1999 World Health Organization.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
