var title_f3_42_3757="Comparison pharm stress agents";
var content_f3_42_3757=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F71371&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F71371&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Comparison of major pharmacologic agents used for stress testing",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"5\" width=\"20%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dipyridamole",
"       </td>",
"       <td class=\"subtitle1\">",
"        Adenosine",
"       </td>",
"       <td class=\"subtitle1\">",
"        Regadenoson",
"       </td>",
"       <td class=\"subtitle1\">",
"        Dobutamine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Chemical",
"        </strong>",
"       </td>",
"       <td>",
"        Pyrimidine derivative",
"       </td>",
"       <td>",
"        Endogenous vasodilator of purine derivative",
"       </td>",
"       <td>",
"        Purine derivative",
"       </td>",
"       <td>",
"        Synthetic catecholamine",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Onset and duration of action, half-life",
"        </strong>",
"       </td>",
"       <td>",
"        Effect peaks at 7 to 15 minutes, half-life 30 to 45 minutes",
"       </td>",
"       <td>",
"        Immediate onset, half-life less than five seconds, effects disappear rapidly after infusion",
"       </td>",
"       <td>",
"        <p>",
"         Peak 1 to 4 minutes after injection",
"        </p>",
"        Half-life of &cong;30 minutes",
"       </td>",
"       <td>",
"        Onset 1 to 2 minutes, half-life 2 minutes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Mechanism of action",
"        </strong>",
"       </td>",
"       <td>",
"        Blocks reuptake of endogenous adenosine causing coronary vasodilation",
"       </td>",
"       <td>",
"        Stimulation of adenosine receptor A2A causing coronary vasodilation",
"       </td>",
"       <td>",
"        Stimulates A2A adenosine receptor causes coronary vasodilation",
"       </td>",
"       <td>",
"        Alpha-1, beta-1, and beta-2 stimulation increases myocardial O2 demand and secondary vasodilatation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Dose",
"        </strong>",
"       </td>",
"       <td>",
"        140 mcg/kg per min for 4 minutes (maximum 0.56 mg/kg)",
"       </td>",
"       <td>",
"        140 mcg/kg per min for 4 to 6 minutes",
"       </td>",
"       <td>",
"        Regadenoson 0.4 mg/5 mL",
"       </td>",
"       <td>",
"        5 to 40 mcg/kg per min, depending upon heart rate response",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Radionuclide injection",
"        </strong>",
"       </td>",
"       <td>",
"        7 to 9 minutes after initiation of infusion",
"       </td>",
"       <td>",
"        3 minutes into infusion; infusion continued for further 1 to 3 minutes",
"       </td>",
"       <td>",
"        10 to 20 seconds after regadenoson",
"       </td>",
"       <td>",
"        At peak stress",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Hemodynamics",
"        </strong>",
"       </td>",
"       <td>",
"        Slight increase in heart rate and slight decrease in blood pressure (BP)",
"       </td>",
"       <td>",
"        Slight increase in heart rate and slight decrease in BP (adenosine more than dipyridamole)",
"       </td>",
"       <td>",
"        Slight increase in heart rate and slight decrease in BP",
"       </td>",
"       <td>",
"        Target heart rate 85 percent of maximum predicted heart rate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Side effects",
"        </strong>",
"       </td>",
"       <td>",
"        Occur frequently, but minor",
"       </td>",
"       <td>",
"        Same as dipyridamole but resolve rapidly; heart block more common",
"       </td>",
"       <td>",
"        Dyspnea, headache and flushing",
"       </td>",
"       <td>",
"        Most common chest pain, most serious nonsustained ventricular tachycardia, nonfatal myocardial infarction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Contraindications",
"        </strong>",
"       </td>",
"       <td>",
"        Bronchospasm, second or third degree AV block or sick sinus syndrome (unless protected by a functioning pacemaker)",
"       </td>",
"       <td>",
"        Same as dipyridamole",
"       </td>",
"       <td>",
"        <p>",
"         Second- or third-degree AV block or sinus node dysfunction",
"        </p>",
"        Caution for bronchospasm",
"       </td>",
"       <td>",
"        Recent acute coronary syndrome, hemodynamic and electrophysiologic instability",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
