var title_f3_45_3803="Antibiotic regimens endometriti";
var content_f3_45_3803=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F64669&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F64669&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Sample antibiotic regimens for endometritis alternatives to the \"Gold Standard\" gentamicin and clindamycin",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Ampicillin-sulbactam 3 grams IV Q 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ticarcillin-clavulanate 3.1 grams IV Q 4 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Cefoxitin 2 grams IV Q 6 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Ceftriaxone 2 grams IV Q 24 hours plus metronidazole 500 mg PO or IV Q 8 hours*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Levofloxacin 500 mg IV Q 24 hours plus metronidazole 500 mg PO or IV Q 8 hours*",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    If chlamydia infection is suspected, azithromycin 1 gram PO for one dose should be added to the regimen.",
"    <div class=\"footnotes\">",
"     * Should not be given to breastfeeding mothers.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
