" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Mycoplasma pneumoniae infection in adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?3/5/3162/contributors\">",
