var title_f3_5_3164="MEN1 vs RET tests";
var content_f3_5_3164=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F80814&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F80814&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Features of MEN1 versus RET germline mutation tests",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Test feature",
"       </td>",
"       <td class=\"subtitle1\">",
"        MEN1 gene",
"       </td>",
"       <td class=\"subtitle1\">",
"        RET gene",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Information to patient and physician",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Guides intervention to prevent cancer",
"       </td>",
"       <td>",
"        No",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Guides intervention to cure cancer",
"       </td>",
"       <td>",
"        No",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Recommended for child",
"       </td>",
"       <td>",
"        Maybe*",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chromosomal locus of gene",
"       </td>",
"       <td>",
"        11q13",
"       </td>",
"       <td>",
"        10cen&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mutation type to cause tumor",
"       </td>",
"       <td>",
"        Inactivate",
"       </td>",
"       <td>",
"        Activate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Genotype/phenotype correlation",
"       </td>",
"       <td>",
"        No",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mutation test shortcuts",
"       </td>",
"       <td>",
"        No",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        False negative rate",
"       </td>",
"       <td>",
"        10-20 percent",
"       </td>",
"       <td>",
"        2-5 percent",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Use in child depends partly on philosophy about using a nonessential genetic test in a subject that is not old enough to make an important long-term decision.",
"     <br>",
"      &bull; 10 cen, chromosome 10, centromeric region.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Brandi, ML, Gagel, RF, Angeli, A, et al. Consensus: Guidelines for diagnosis and therapy of MEN type 2. J Clin Endocrinol Metab 2001; 86:5668. Copyright &copy; 2001 the Endocrine Society.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
