" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Postpolypectomy electrocoagulation syndrome",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?3/56/3971/contributors\">",
