var title_f3_59_4028="Oral abx paronychia";
var content_f3_59_4028=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F80408&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F80408&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Empiric therapy for paronychia due to presumed oral flora",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Agent of choice",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Amoxicillin - clavulanate (875/125 mg twice daily)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Alternate empiric regimens include:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       One of the following agents with activity against",
"       <em>",
"        Eikenella",
"       </em>",
"       :",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Doxycycline* (100 mg twice daily)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Trimethoprim-sulfamethoxazole* (1 double strength tablet twice daily)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Penicillin VK (500 mg four times daily)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Cefuroxime (500 mg twice daily)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       <p>",
"        Fluoroquinolones (one of the following):",
"       </p>",
"       <p>",
"        &bull; Ciprofloxacin (500 to 750 mg twice daily)",
"       </p>",
"       <p>",
"        &bull; Moxifloxacin (400 mg once daily)",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       <span class=\"red\">",
"        <strong>",
"         PLUS",
"        </strong>",
"       </span>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       One of the following agents with anaerobic activity:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Metronidazole (500 mg three times daily)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Clindamycin* (450 mg three times daily)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       The following agents have poor activity against",
"       <em>",
"        Eikenella",
"       </em>",
"       and should be avoided:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Cephalexin",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Dicloxacillin",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Erythromycin",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       &nbsp;",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Also active against MRSA (check susceptibility testing).",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
