var title_f3_59_4029="Causes abd pain";
var content_f3_59_4029=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F67102&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F67102&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Classification of causes of abdominal pain",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <tr>",
"      <td class=\"container\">",
"       <table cellspacing=\"0\">",
"        <tr>",
"         <td class=\"subtitle1_single\">",
"          Visceral",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"subtitle2_single\">",
"          1. Mechanical obstruction of hollow viscera",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          a. Obstruction of the small or large intestine",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          b. Obstruction of the biliary tree",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          c. Obstruction of the ureter",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"subtitle2_single\">",
"          2. Distention of visceral surfaces, eg, hepatic or renal capsules",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"subtitle2_single\">",
"          3. Vascular disturbances",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          a. Embolism or thrombosis",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          b. Vascular rupture",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          c. Pressure or torsional occlusion",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          d. Sickle cell anemia",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"subtitle2_single\">",
"          4. Referred from non-abdominal viscera",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          a. Thorax, eg, pneumonia, referred pain from coronary occlusion",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"subtitle2_single\">",
"          5. Metabolic causes",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          a. Black widow spider bite",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          b. Lead poisoning and others",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          c. Uremia",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          b. Diabetic ketoacidosis",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          c. Porphyria",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          d. Allergic factors (C'1 esterase inhibitor deficiency)",
"         </td>",
"        </tr>",
"       </table>",
"      </td>",
"      <td class=\"container\">",
"       <table cellspacing=\"0\">",
"        <tr>",
"         <td class=\"subtitle1_single\">",
"          Somatic",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"subtitle2_single\">",
"          1. Parietal peritoneal inflammation",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          a. Bacterial contamination, eg, perforated appendix, pelvic inflammatory disease",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          b. Chemical irritation, eg, perforated ulcer, pancreatitis, mittelschmerz",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"subtitle2_single\">",
"          2. Abdominal wall",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          a. Distortion or traction of mesentery",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          b. Trauma or infection of muscles",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"subtitle2_single\">",
"          3. Spine, eg, radiculitis from arthritis",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"subtitle2_single\">",
"          4. Neurogenic causes",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          a. Tabes dorsalis",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          b. Herpes zoster",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          c. Causalgia and others",
"         </td>",
"        </tr>",
"        <tr>",
"         <td class=\"indent1\">",
"          d. Functional",
"         </td>",
"        </tr>",
"       </table>",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Howard, FM. Acute Abdominal Pain. In: Primary Care for Women., Leppert, PC, Peipert, P (Eds), Lippincott, Williams and Wilkins 2004. Copyright &copy;2004 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
