" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment of knee injuries in the young athlete",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?3/61/4056/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?3/61/4056/contributors\">",
"     Albert C Hergenroeder, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?3/61/4056/contributors\">",
"     Section Editors",
