var title_f3_61_4060="Home playground safety";
var content_f3_61_4060=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F50855&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F50855&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Consumer product safety commission recommendations for home playground safety",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       A shock-absorbing surface should be installed around the play equipment and regularly maintained:",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       If wood chips, mulch, or shredded rubber is used, a depth of at least 9 inches should be maintained for play equipment up to 7 feet high",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       If sand or pea gravel is used, a depth of at least 9 inches should be maintained for play equipment up to 5 feet high",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Alternatively, surfacing mats made of",
"       <strong>",
"        safety-tested",
"       </strong>",
"       rubber or rubber-like material can be used",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Install protective surfacing at least 6 feet in all directions from play equipment. For swings, be sure surfacing extends in back and front, twice the height of the suspending bar.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       <strong>",
"        Never",
"       </strong>",
"       attach--or allow children to attach--ropes, jump ropes, clotheslines, or pet leashes to play equipment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Check for hazardous hardware (eg, open \"S\" hooks or protruding bolt ends)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Check for openings in guardrails or between ladder rungs that could trap children; these spaces should measure less than 3.5 inches or more than 9 inches",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Make sure platforms and ramps have guardrails to prevent falls",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Check for sharp points or edges in equipment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Remove tripping hazards, like exposed concrete footings, tree stumps, and rocks",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Regularly check play equipment and surfacing to make sure both are in good condition",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Carefully supervise children on play equipment to make sure they are safe",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Consumer Product Safety Commission. Home Playground Equipment-Related Deaths and Injuries. CPSC Document available at file://www.cpsc.gov/playgrnd.html.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
