" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Stress-induced (takotsubo) cardiomyopathy",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
