" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical features and treatment of ventricular arrhythmias during acute myocardial infarction",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?30/10/30890/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?30/10/30890/contributors\">",
"     Philip J Podrid, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?30/10/30890/contributors\">",
"     Leonard I Ganz, MD, FHRS, FACC",
"    </a>",
"    <br/>",
