var title_f30_13_30941="Irritant contact dermatitis clinical classification";
var content_f30_13_30941=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F81138&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F81138&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical classification of irritant contact dermatitis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Type",
"       </td>",
"       <td class=\"subtitle1\">",
"        History",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical manifestation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Prognosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Acute ICD",
"       </td>",
"       <td>",
"        <p>",
"         Acute exposure, usually accidental, to potent irritants (strong acids or alkalis)",
"        </p>",
"        <p>",
"         May develop within minutes after exposure to powerful irritants",
"        </p>",
"        <p>",
"         Nearly all exposed individuals develop skin lesions",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Erythema, edema, vesicles, bullae, epidermal necrosis",
"        </p>",
"        <p>",
"         Symptoms: stinging, burning, and pain",
"        </p>",
"        <p>",
"         Decrescendo phenomenon: the healing process begins soon after the removal of the offending substance",
"        </p>",
"       </td>",
"       <td>",
"        Good",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Delayed acute ICD",
"       </td>",
"       <td>",
"        <p>",
"         Delayed onset (8-24 hrs after exposure)",
"        </p>",
"        <p>",
"         Some chemicals, such as dithranol (anthralin), podophyllin, epichlorhydrin, propane sulfone, ethylene oxide, and benzalkonium chloride",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Similar clinical manifestation to acute ICD",
"        </p>",
"        <p>",
"         Patch testing: highly recommended for differential diagnosis from ACD",
"        </p>",
"       </td>",
"       <td>",
"        Good",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Irritant reaction",
"       </td>",
"       <td>",
"        <p>",
"         High-risk groups: wet-work workers such as hairdressers, food preparation workers, bar workers, new mothers, or cleaning personnel",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Mild erythema, vesicles or erosions, dryness, chapping, and scaling",
"        </p>",
"        <p>",
"         Usually occur under occlusive areas",
"        </p>",
"        <p>",
"         May evolve to chronic cumulative ICD",
"        </p>",
"       </td>",
"       <td>",
"        Good",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Chronic cumulative ICD&nbsp;",
"       </td>",
"       <td>",
"        <p>",
"         Most common type of ICD",
"        </p>",
"        <p>",
"         Repeated&nbsp;exposure to weak irritants (soaps, detergents, surfactants, solvents,&nbsp;or oils)",
"        </p>",
"        <p>",
"         High-risk groups: hairdressers, healthcare personnel, cleaning workers, and metal workers",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Erythema and dryness that progress to hyperkeratosis, fissuring, and lichenification",
"        </p>",
"        <p>",
"         Patch testing: highly recommended for differential diagnosis from ACD",
"        </p>",
"       </td>",
"       <td>",
"        Variable",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Traumatic ICD",
"       </td>",
"       <td>",
"        Slower onset after laceration, burn, or acute ICD",
"       </td>",
"       <td>",
"        <p>",
"         Erythema, vesicles or papules, and scaling",
"        </p>",
"        <p>",
"         Prolonged healing time",
"        </p>",
"       </td>",
"       <td>",
"        Variable",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Acneiform or pustular ICD",
"       </td>",
"       <td>",
"        <p>",
"         Rare, slow onset",
"        </p>",
"        <p>",
"         Tar, oils, naphthalenes, asphalt, chlorinated agents, or grease",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         &nbsp;",
"        </p>",
"        <p>",
"         Papular or pustular lesions",
"        </p>",
"       </td>",
"       <td>",
"        Good",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Non-erythematous (suberythematous) irritation",
"       </td>",
"       <td>",
"        <p>",
"         Slow onset",
"        </p>",
"        <p>",
"         Surfactants in soaps, detergents, or cosmetics (cocoamidopropyl betaine, coconut diethanolamide)",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Subjective discomfort such as stinging, itching, or burning, without visible clinical signs",
"        </p>",
"        <p>",
"         Histologic changes",
"        </p>",
"       </td>",
"       <td>",
"        Variable",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Sensory (subjective) irritation",
"       </td>",
"       <td>",
"        <p>",
"         Acute onset",
"        </p>",
"        <p>",
"         Mostly in middle-aged women",
"        </p>",
"        <p>",
"         Mainly cosmetic products containing&nbsp;lactic acid or sorbic acid",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Lack of clinical signs of inflammation",
"        </p>",
"        <p>",
"         Subjective feeling of stinging, itching, and burning",
"        </p>",
"       </td>",
"       <td>",
"        Excellent",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Friction irritation",
"       </td>",
"       <td>",
"        <p>",
"         Slow onset",
"        </p>",
"        <p>",
"         Repeated microtrauma and friction from&nbsp;handling wood, plants, coarse paper, or&nbsp;rock wool&nbsp;",
"        </p>",
"       </td>",
"       <td>",
"        Lichenification, hyperkeratosis",
"       </td>",
"       <td>",
"        Variable",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Asteatotic irritant dermatitis",
"       </td>",
"       <td>",
"        <p>",
"         Slow onset",
"        </p>",
"        <p>",
"         High risk group: elderly",
"        </p>",
"        <p>",
"         Mostly in winter",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Erythema, dry and cracked skin",
"        </p>",
"        <p>",
"         Intense itching",
"        </p>",
"       </td>",
"       <td>",
"        Variable",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from:",
"     <br>",
"      <ol>",
"       <li>",
"        Watkins SA, Maibach HI. The hardening phenomenon in irritant contact dermatitis: An interpretative update. Contact Dermatitis 2009; 60:123.",
"       </li>",
"       <li>",
"        Amado A, Taylor JS, Sood A. Irritant Contact Dermatitis. In: Fitzpatrick's Dermatology in General Medicine, 7th ed, Wolff K, Goldsmith LA, Katz SI, et al (Eds), McGraw Hill Medical 2008. p.395.",
"       </li>",
"      </ol>",
"     </br>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
