" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Chronic obstructive pulmonary disease: Definition, clinical manifestations, diagnosis, and staging",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?30/14/30954/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?30/14/30954/contributors\">",
"     Stephen I Rennard, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?30/14/30954/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?30/14/30954/contributors\">",
"     James K Stoller, MS, MD",
"    </a>",
"    <br/>",
"   </div>",
