var title_f30_15_30973="Muscles of the posterior compartment of the forearm";
var content_f30_15_30973=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F63813&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F63813&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Muscles of the posterior compartment of the forearm",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Muscle",
"       </td>",
"       <td class=\"subtitle1\">",
"        Proximal attachment",
"       </td>",
"       <td class=\"subtitle1\">",
"        Distal attachment",
"       </td>",
"       <td class=\"subtitle1\">",
"        Innervation*",
"       </td>",
"       <td class=\"subtitle1\">",
"        Main action",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"5\">",
"        Superficial layer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Brachioradialis",
"       </td>",
"       <td>",
"        Proximal two thirds of supraepicondylar ridge of humerus",
"       </td>",
"       <td>",
"        Lateral surface of distal end of radius proximal to styloid process",
"       </td>",
"       <td>",
"        Radial nerve (C5,",
"        <strong>",
"         C6",
"        </strong>",
"        , C7)",
"       </td>",
"       <td>",
"        Relatively week flexion of forearm, maximal when forearm is in midpronated position",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Extensor carpi radialis longus (ECRL)",
"       </td>",
"       <td>",
"        Lateral supraepicondylar ridge of humerus",
"       </td>",
"       <td>",
"        Dorsal aspect of base of 2nd metacarpal",
"       </td>",
"       <td>",
"        Radial nerve (C6, C7)",
"       </td>",
"       <td rowspan=\"2\">",
"        Extend and abduct hand at the wrist joint; ECRL active during fist clenching",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Extensor carpi radialis brevis (ECRB)",
"       </td>",
"       <td rowspan=\"3\">",
"        Lateral epicondyle of humerus (common extensor origin)",
"       </td>",
"       <td>",
"        Doral aspect of base of 3rd metacarpal",
"       </td>",
"       <td>",
"        Deep branch of radial nerve (",
"        <strong>",
"         C7",
"        </strong>",
"        , C8)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Extensor digitorum",
"       </td>",
"       <td>",
"        Extensor expansions of medial four fingers",
"       </td>",
"       <td rowspan=\"3\">",
"        Posterior interosseous nerve (",
"        <strong>",
"         C7",
"        </strong>",
"        , C8), continuation of deep branch of radial nerve",
"       </td>",
"       <td>",
"        Extends medial four fingers primarily at metacarpophalangeal joints, secondarily at interphalangeal joints",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Extensor digiti minimi (EDM)",
"       </td>",
"       <td>",
"        Extensor expansion of 5th finger",
"       </td>",
"       <td>",
"        Extends 5th finger primarily at metacarpophalangeal joint, secondarily at interphalangeal joint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Extensor carpi ulnaris (ECU)",
"       </td>",
"       <td>",
"        Lateral epicondyle of humerus; posterior border of ulna via a shared aponeurosis",
"       </td>",
"       <td>",
"        Dorsal aspect of base of 5th metacarpal",
"       </td>",
"       <td>",
"        Extends and adducts hand at wrist joint (also active during fist clenching)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"5\">",
"        Deep layer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Supinator",
"       </td>",
"       <td>",
"        Lateral epicondyle of humerus; radial collateral and anular ligaments; supinator fossa; crest of ulna",
"       </td>",
"       <td>",
"        Lateral posterior, and anterior surfaces of proximal third of radius",
"       </td>",
"       <td>",
"        Deep branch of radial nerve (C7,",
"        <strong>",
"         C8",
"        </strong>",
"        )",
"       </td>",
"       <td>",
"        Supinates forearm; rotates radius to turn palm anteriorly or superiorly (if elbow is flexed)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Extensor indicis",
"       </td>",
"       <td>",
"        Posterior surface of distal third of ulna and interosseous membrane",
"       </td>",
"       <td>",
"        Extensor expansion of 2nd finger",
"       </td>",
"       <td>",
"        Posterior interosseous nerve (C7,",
"        <strong>",
"         C8",
"        </strong>",
"        ), continuation of deep branch of radial nerve",
"       </td>",
"       <td>",
"        Extends 2nd finger (enabling its independent extension); helps extend hand at wrist",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"5\">",
"        Outcropping muscles of deep layer",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Abductor pollicis longus (APL)",
"       </td>",
"       <td>",
"        Posterior surface of proximal halves of ulna, radius, and interosseous membrane",
"       </td>",
"       <td>",
"        Base of 1st metacarpal",
"       </td>",
"       <td rowspan=\"3\">",
"        Posterior interosseous nerve (C7,",
"        <strong>",
"         C8",
"        </strong>",
"        ), continuation of deep branch of radial nerve",
"       </td>",
"       <td>",
"        Abducts thumb and extends it at carpometacarpal joint",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Extensor pollicis longus (EPL)",
"       </td>",
"       <td>",
"        Posterior surface of middle third of ulna and interosseous membrane",
"       </td>",
"       <td>",
"        Dorsal aspect of base of distal phalanx of thumb",
"       </td>",
"       <td>",
"        Extends distal phalanx of thumb at interphalangeal joint; extends metacarpophalangeal and carpometacarpal joints",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Extensor pollicis brevis (EPB)",
"       </td>",
"       <td>",
"        Posterior surface of distal third of radius and interosseous membrane",
"       </td>",
"       <td>",
"        Dorsal aspect of base of proximal phalanx of thumb",
"       </td>",
"       <td>",
"        Extends proximal phalanx of thumb at metacarpophalangeal joint; extends carpometacarpal joint",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * The spinal cord segmental innervation is indicated (eg, \"",
"     <strong>",
"      C7",
"     </strong>",
"     , C8\" means that the nerves supplying the extensor carpi radialis brevis are derived from the seventh and eighth cervical segments of the spinal cord). Numbers in boldface (",
"     <strong>",
"      C7",
"     </strong>",
"     ) indicate the main segmental innervation. Damage to one or more of the listed spinal cord segments or to the motor nerve roots arising from them results in paralysis of the muscles concerned.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Moore KL, Dalley AR. Clinically Oriented Anatomy, 5th ed, Lippincott Williams &amp; Wilkins, Philadelphia 2006. Copyright &copy; 2006 Lippincott Williams &amp; Wilkins.",
"    </div>",
"    <div class=\"contractual\">",
"     <br/>",
"     <a href=\"file://www.lww.com\">",
"      file://www.lww.com",
"     </a>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
