"    <li class=\"plainItem\">",
"     <a href=\"#references\">",
"      REFERENCES",
"     </a>",
"    </li>",
"   </ul>",
"  </div>",
"  <h1>",
"   RELATED TOPICS",
"  </h1>",
"  <div id=\"relatedTopics\">",
