var title_f30_19_31037="ASGE guidelines Abx prophylxs";
var content_f30_19_31037=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F51133&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F51133&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Antibiotic prophylaxis for endoscopic procedures",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Patient condition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Procedure contemplated",
"       </td>",
"       <td class=\"subtitle1\">",
"        Antibiotic prophylaxis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"3\">",
"        High risk:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\" rowspan=\"2\">",
"        <p>",
"         Prosthetic heart valve or prosthetic material used for valve repair",
"        </p>",
"        <p>",
"         History of endocarditis",
"        </p>",
"        <p>",
"         Unrepaired cyanotic congenital heart disease (including palliative shunts and conduits)",
"        </p>",
"        <p>",
"         Repaired congential heart disease with residual defects at the site of or adjacent to a prosthetic device",
"        </p>",
"        <p>",
"         Completely repaired congenital heart defects with prosthetic material or device during the first six months after the repair",
"        </p>",
"        <p>",
"         Cardiac valvulopathy in a transplanted heart",
"        </p>",
"       </td>",
"       <td class=\"sublist_other\">",
"        <p>",
"         Stricture dilation",
"        </p>",
"        <p>",
"         Variceal sclerotherapy",
"        </p>",
"        <p>",
"         ERCP/obstructed biliary tree",
"        </p>",
"       </td>",
"       <td class=\"sublist_other\">",
"        Not recommended",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\">",
"        Other endoscopic procedures, including EGD and colonoscopy (with or without biopsy/polypectomy), variceal ligation",
"       </td>",
"       <td class=\"sublist_other\">",
"        Not recommended",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"sublist1_start\" colspan=\"3\">",
"        Moderate risk:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\" rowspan=\"2\">",
"        <p>",
"         Most other congenital abnormalities",
"        </p>",
"        <p>",
"         Acquired valvular dysfunction (eg, rheumatic heart disease)",
"        </p>",
"        <p>",
"         Hypertrophic cardiomyopathy",
"        </p>",
"        <p>",
"         Mitral valve prolapse with regurgitation or thickened leaflets",
"        </p>",
"       </td>",
"       <td class=\"sublist_other\">",
"        <p>",
"         Esophageal stricture dilation",
"        </p>",
"        <p>",
"         Variceal sclerotherapy",
"        </p>",
"       </td>",
"       <td class=\"sublist_other\">",
"        Not recommended",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist_other\">",
"        Other endoscopic procedures, including EGD and colonoscopy (with or without biopsy/polypectomy), variceal ligation",
"       </td>",
"       <td class=\"sublist_other\">",
"        Not recommended",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td class=\"sublist1_start\" colspan=\"3\">",
"        Low risk:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Other cardiac conditions (CABG, repaired septal defect or patent ductus, mitral valve prolapse without valvular regurgitation, isolated secundum atrial septal defect, physiologic/functional/innocent heart murmurs, rheumatic fever without valvular dysfunction, pacemakers, implantable defibrillators)",
"       </td>",
"       <td class=\"sublist_other\">",
"        All endoscopic procedures",
"       </td>",
"       <td class=\"sublist_other\">",
"        Not recommended",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Obstructed bile duct without cholangitis",
"       </td>",
"       <td>",
"        ERCP with complete drainage",
"       </td>",
"       <td>",
"        Not recommended",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Obstructed bile duct with cholangitis",
"       </td>",
"       <td>",
"        ERCP with anticipated incomplete drainage",
"       </td>",
"       <td>",
"        Recommended (continue antibiotics after procedure)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Pancreatic cystic lesion",
"       </td>",
"       <td>",
"        ERCP, EUS-FNA",
"       </td>",
"       <td>",
"        Recommended",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Cirrhosis acute gastrointestinal bleed (required for patients with or without endoscopic procedures)",
"       </td>",
"       <td>",
"        All endoscopic procedures",
"       </td>",
"       <td>",
"        Recommended",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Ascites, immunocompromised patient",
"       </td>",
"       <td>",
"        <p>",
"         Stricture dilation",
"        </p>",
"        <p>",
"         Variceal sclerotherapy",
"        </p>",
"        <p>",
"         Other endoscopic procedures, including EGD and colonoscopy (with or without biopsy/polypectomy), variceal ligation",
"        </p>",
"       </td>",
"       <td>",
"        No recommendation",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        All patients",
"       </td>",
"       <td>",
"        Percutaneous endoscopic feeding tube placement",
"       </td>",
"       <td>",
"        Recommended (parenteral cephalosporin or equivalent)",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Vascular graft",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"       <td>",
"        <p>",
"         AHA: Recommended antibiotic usage within 6 months of procedure",
"        </p>",
"        <p>",
"         ASGE: Antibiotics not recommended",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Prosthetic joints",
"       </td>",
"       <td>",
"        All endoscopic procedures",
"       </td>",
"       <td>",
"        Not recommended",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    This summary table is based upon recommendations from the American Society of Gastrointestinal Endoscopy (Banerjee, S, Shen, B, Baron, T, et al. Gastrointest Endosc 2008; 67:791) and the American Heart Association (Wilson, W, Taubert, KA, Gewitz, M, et al. Circulation 2007; 116:1736 and Nishimura, RA, Carabello, BA, Faxon, DP, et al. Circulation 2008; 118:887). NOTE: See other table (\"Antibiotic regimens: Prophylaxis for endoscopic procedures\") for specific regimens.",
"    <div class=\"footnotes\">",
"     CABG: coronary artery bypass graft; ERCP: endoscopic retrograde cholangiopancreatography; EGD: esophagogastroduodenoscopy; EUS-FNA: endoscopic ultrasound with fine needle aspiration; AHA: American Heart Association; ASGE: American Society for Gastrointestinal Endoscopy.",
"    </div>",
"    <div class=\"reference\">",
"     Modified with permission from: Hirota WK, Petersen K, Baron TH, et al. Guidelines for Antibiotic Prophylaxis for GI Endoscopy. Gastrointest Endosc 2003; 58:475. Copyright &copy; 2003 Elsevier.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
