var title_f30_2_30764="Guidelines cGVHD";
var content_f30_2_30764=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F87217&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=11\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F87217&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=11\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Guidelines for screening, prevention, and management of late complications in patients with oral cGVHD",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Late complication",
"       </td>",
"       <td class=\"subtitle1\">",
"        Prevention",
"       </td>",
"       <td class=\"subtitle1\">",
"        Screening",
"       </td>",
"       <td class=\"subtitle1\">",
"        Management",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Oral squamous cell carcinoma",
"       </td>",
"       <td>",
"        <p>",
"         Smoking cessation",
"        </p>",
"        Moderate alcohol consumption",
"       </td>",
"       <td>",
"        <p>",
"         Annual clinical examination",
"        </p>",
"        Biopsy of atypical/suspicious lesions",
"       </td>",
"       <td>",
"        Referral to multidisciplinary head and neck oncology center",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Rampant dental caries",
"       </td>",
"       <td>",
"        <p>",
"         Minimize intake of refined carbohydrates (especially sugar-containing soft drinks)",
"        </p>",
"        <p>",
"         Brush at least twice daily, after eating when possible",
"        </p>",
"        <p>",
"         Floss daily",
"        </p>",
"        <p>",
"         Fluoride 1.1 percent gel paint on or in custom trays, daily",
"        </p>",
"        <p>",
"         Remineralizing agent, apply with fluoride",
"        </p>",
"        Professional fluoride varnish application",
"       </td>",
"       <td>",
"        <p>",
"         Increased risk in patients with significant salivary gland cGVHD",
"        </p>",
"        <p>",
"         Increased risk in patients with orofacial sclerotic cGVHD",
"        </p>",
"        <p>",
"         Increased risk in patients were severe mucosal disease and avoidance of oral hygiene",
"        </p>",
"        <p>",
"         Examine teeth for evidence of cervical demineralization/decay",
"        </p>",
"        Twice annual dental visits",
"        <br/>",
"        <ul>",
"         <li>",
"          Soft and hard tissue examination",
"         </li>",
"         <li>",
"          Bitewing radiographs (annual)",
"         </li>",
"        </ul>",
"       </td>",
"       <td>",
"        <p>",
"         Treat dental caries as soon as diagnosed",
"        </p>",
"        <p>",
"         Careful follow-up for new or recurrent caries",
"        </p>",
"        <p>",
"         Reinforce oral hygiene and dietary habits",
"        </p>",
"        Reinforce daily preventive measures",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Fibrosis",
"       </td>",
"       <td>",
"        No known preventive measures",
"       </td>",
"       <td>",
"        <p>",
"         Ask patient if aware of tightness/limited opening",
"        </p>",
"        <p>",
"         Extensive sclerotic skin disease, especially with neck involved",
"        </p>",
"        Examine for intraoral buccal fibrotic bands by palpation",
"       </td>",
"       <td>",
"        <p>",
"         Physical therapy",
"        </p>",
"        <p>",
"         Intralesional steroid therapy",
"        </p>",
"        <p>",
"         Surgery",
"        </p>",
"        Systemic therapy for systemic involvement",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     cGVHD: chronic graft versus host disease.",
"    </div>",
"    <div class=\"reference\">",
"     This research was originally published in Blood. Treister N, Duncan C, Cutler C, Lehmann L. How we treat oral chronic graft-versus-host disease. Blood 2012; 120:3407. Copyright &copy; 2012 American Society of Hematology.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
