var title_f30_21_31067="Cantu guideline for concussion management";
var content_f30_21_31067=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F68019&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F68019&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Cantu guideline for concussion management",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Grade 1",
"       </td>",
"       <td class=\"subtitle1\">",
"        Grade 2",
"       </td>",
"       <td class=\"subtitle1\">",
"        Grade 3",
"       </td>",
"      </tr>",
"      <tr>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        Presentation",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         1. No loss of consciousness",
"        </p>",
"        <p>",
"         2. Post-traumatic amnesia or other signs lasting less than 30 minutes",
"        </p>",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         1. Loss of consciousness for less than 1 minute",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         2. Post-traumatic amnesia or other symptoms for more than 30 minutes, less than 24 hours",
"        </p>",
"       </td>",
"       <td colspan=\"1\" rowspan=\"1\">",
"        <p>",
"         1. Loss of consciousness for longer than 1 minute",
"        </p>",
"        <p>",
"         <strong>",
"          OR",
"         </strong>",
"        </p>",
"        <p>",
"         2. Post-traumatic amnesia or other symptoms for longer than 24 hours",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Management",
"       </td>",
"       <td>",
"        Athlete may return to play if asymptomatic for one week",
"       </td>",
"       <td>",
"        Athlete may return to play in 2 weeks if asymptomatic at rest and on exertion for 7 days",
"       </td>",
"       <td>",
"        Athlete may return to play in one month if asymptomatic at rest and on exertion for 7 days",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Cantu, RC, J Athl Train 2001; 36:244.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
