var title_f30_22_31083="Clinical circumstances for ICD evaluation";
var content_f30_22_31083=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F79427&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F79427&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical circumstances suggesting the need for implantable cardioverter-defibrillator evaluation",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Clinical Situation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Considerations",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Recurrent shocks, therapies",
"       </td>",
"       <td>",
"        Lead failure, inappropriate therapies for atrial arrhythmias, recurrent or refractory ventricular arrhythmias",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        New antiarrhythmic drugs (affect defibrillation threshold or ventricular tachycardia rate, or both)",
"       </td>",
"       <td>",
"        Antiarrhythmic drugs can alter ventricular tachycardia rate, requiring reprogramming; can also affect defibrillation threshold",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Significant change in clinical status: myocardial infarction, deteriorating cardiac function",
"       </td>",
"       <td>",
"        Can affect sensing (if infarction involves tissue near",
"sensing lead); require new medications, rarely, affect defibrillation",
"threshold",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Reprogramming to a lower sensitivity (eg, for T wave oversensing)",
"       </td>",
"       <td>",
"        Might result in underdetection of ventricular fibrillation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Other events: such as pneumonectomy or patch crinkle",
"       </td>",
"       <td>",
"        Can affect defibrillation threshold",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from Hayes, DL, Friedman, PA. In: Cardiac Pacing and Difibrillation, Hayes, DL, Lloyd, MA, Friedman, PA (Eds), Futura Publishing Company, Inc. Armonk, NY 2000. pp.541-587. Copyright &copy; 2001 Futura Publishing Company, Inc.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
