" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Normal B and T lymphocyte development",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?30/23/31098/contributors\">",
