var title_f30_23_31099="Viral hepatitis prevention";
var content_f30_23_31099=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=GAST%2F64865&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=GAST%2F64865&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Viral hepatitis prevention board",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Seven points consensus on hepatitis B as an occupational hazard",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Occupational hepatitis B vaccination programs should apply equally to employees in both the public and private sectors, self-employed staff, students, contract workers and voluntary workers.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       The employer must ensure that the risk of occupational transmission is assessed. The assessment should be based on work practices and should be independent of job title or status.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       If the assessment shows the worker is at risk of contamination, the employer has the obligation to inform the worker at risk.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Information, training and education must be provided by the employer for the above workers on the measures to be taken to protect them from risk.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       The employer must offer the above workers vaccination free of charge.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       The employer has an obligation to ensure that exposed workers are correctly vaccinated. Employees who decline vaccination must be counseled and sign a refusal form.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Current practices should be improved by providing information to increase awareness and change attitudes, thereby improving acceptance of vaccination. Information and education should be targeted at occupational health professionals, employers and managers, workers, health and safety representatives and unions.",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     From: Hallower J, Kane M, McCloy E (Eds). Eliminating Hepatitis B as an occupational hazard. London medical imprint 1993. p.114.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
