" <div class=\"graphic\">",
"  <div class=\"figure\" style=\"width: 517px\">",
"   <div class=\"ttl\">",
"    Recurrent laryngeal nerve variation",
"   </div>",
"   <div class=\"cntnt\" style=\"width: 497px; height: 251px; background-image: url(data:image/jpeg;base64,);\">",
"   </div>",
