var title_f30_24_31117="Rx entero endocarditis";
var content_f30_24_31117=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F50418&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F50418&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Suggested regimens for therapy of native valve endocarditis due to enterococcal strains susceptible to penicillin, gentamicin, and vancomycin",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"4\" width=\"25%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\" colspan=\"2\">",
"        American Heart Association",
"       </td>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        British Society for Antimicrobial Chemotherapy",
"       </td>",
"       <td class=\"subtitle1\" rowspan=\"2\">",
"        European Society of Cardiology*",
"        <sup>",
"         &bull;",
"        </sup>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2\">",
"        Adult",
"        <br/>",
"        (for patients with normal renal function)",
"       </td>",
"       <td class=\"subtitle2\">",
"        Pediatric",
"        <br/>",
"        (not to exceed dose of normal adult)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         Gentamicin",
"         <sup>",
"          &Delta;",
"         </sup>",
"         3 mg/kg per 24 hours IV or IM in three equally divided doses for four to six weeks",
"         <sup>",
"          &loz;",
"         </sup>",
"        </p>",
"        <p>",
"         <strong>",
"          Plus one of the following",
"         </strong>",
"         <sup>",
"          &loz;",
"         </sup>",
"         <strong>",
"          :",
"         </strong>",
"        </p>",
"        <p>",
"         Aqueous penicillin G 18 to 30 million units per 24 hours IV either continuously or in six equally divided doses for four to six weeks",
"         <sup>",
"          &loz;",
"         </sup>",
"        </p>",
"        <p>",
"         <strong>",
"          or",
"         </strong>",
"        </p>",
"        <p>",
"         Ampicillin 12 g per 24 hours IV in six equally divided doses for four to six weeks",
"         <sup>",
"          &loz;",
"         </sup>",
"        </p>",
"        <p>",
"         <strong>",
"          or",
"         </strong>",
"        </p>",
"        <p>",
"         Vancomycin",
"         <sup>",
"          &sect;",
"         </sup>",
"         30 mg/kg per 24 hours IV in two equally divided doses for six weeks; not to exceed 2 g per 24 hours unless concentrations in serum are inappropriately low",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Gentamicin",
"         <sup>",
"          &Delta;",
"         </sup>",
"         3 mg/kg per 24 hours IV or IM in three equally divided doses for four to six weeks",
"         <sup>",
"          &loz;",
"         </sup>",
"        </p>",
"        <p>",
"         <strong>",
"          Plus one of the following:",
"         </strong>",
"        </p>",
"        <p>",
"         Aqueous penicillin G 300,000 units/kg per 24 hours IV in four to six equally divided doses for four to six weeks",
"         <sup>",
"          &loz;",
"         </sup>",
"        </p>",
"        <p>",
"         <strong>",
"          or",
"         </strong>",
"        </p>",
"        <p>",
"         Ampicillin 300 mg/kg per 24 hours IV in four to six equally divided doses for four to six weeks",
"         <sup>",
"          &loz;",
"         </sup>",
"        </p>",
"        <p>",
"         <strong>",
"          or",
"         </strong>",
"        </p>",
"        <p>",
"         Vancomycin",
"         <sup>",
"          &sect;",
"         </sup>",
"         40 mg/kg per 24 hours IV in two or three equally divided doses for six weeks",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Gentamicin",
"         <sup>",
"          &Delta;",
"         </sup>",
"         1 mg/kg every 12 hours IV for four to six weeks",
"        </p>",
"        <p>",
"         <strong>",
"          Plus one of the following:",
"         </strong>",
"        </p>",
"        <p>",
"         Amoxicillin 2 g every four hours IV for four to six weeks",
"        </p>",
"        <p>",
"         <strong>",
"          or",
"         </strong>",
"        </p>",
"        <p>",
"         Penicillin 2.4 g every four hours IV for four to six weeks",
"        </p>",
"        <p>",
"         <strong>",
"          or",
"         </strong>",
"        </p>",
"        <p>",
"         Vancomycin",
"         <sup>",
"          &sect;",
"         </sup>",
"         1 g IV every 12 hours for four to six weeks",
"        </p>",
"        <p>",
"         <strong>",
"          or",
"         </strong>",
"        </p>",
"        <p>",
"         Teicoplanin",
"         <sup>",
"          &yen;",
"         </sup>",
"         10 mg/kg IV once every 24 hours for four to six weeks",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Gentamicin",
"         <sup>",
"          &Delta;",
"         </sup>",
"         3 mg/kg per 24 hours IV or IM in two or three equally divided doses for four to six weeks",
"         <sup>",
"          &loz;",
"         </sup>",
"        </p>",
"        <p>",
"         <strong>",
"          Plus one of the following:",
"         </strong>",
"        </p>",
"        <p>",
"         Amoxicillin 200 mg/kg per 24 hours IV in four to six equally divided doses for four to six weeks",
"         <sup>",
"          &loz;",
"         </sup>",
"        </p>",
"        <p>",
"         <strong>",
"          or",
"         </strong>",
"        </p>",
"        <p>",
"         Ampicillin 12 g per 24 hours (200 mg/kg per 24 hours) IV in six equally divided doses for four to six weeks",
"         <sup>",
"          &loz;",
"         </sup>",
"        </p>",
"        <p>",
"         <strong>",
"          or",
"         </strong>",
"        </p>",
"        <p>",
"         Vancomycin",
"         <sup>",
"          &sect;",
"         </sup>",
"         30 mg/kg per 24 hours IV in two equally divided doses for six weeks",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     IV: intravenously; IM: intramuscularly.",
"     <br/>",
"     * Pediatric doses (should not exceed adult doses): amoxicillin 300 mg/kg per 24 hours IV in four or six divided doses; gentamicin, ampicillin, and vancomycin dosing recommendations are same as American Heart Association, pediatric.",
"     <br/>",
"     <span class=\"bullet\">",
"      &bull;",
"     </span>",
"     Alternative for penicillin-allergic patient provided isolate is teicoplanin susceptible (MIC &le;4 mg/L).",
"     <br/>",
"     &Delta; Renal function and gentamicin serum concentrations should be monitored at least once per week. Gentamicin dosage adjusted for peak serum concentrations 3 to 4 mcg/mL, trough &lt;1 mcg/mL when two to three divided doses used.",
"     <br/>",
"     <span class=\"lozenge\">",
"      &loz;",
"     </span>",
"     Four weeks of therapy recommended for patients with symptoms of illness &le;3 months; six weeks of therapy recommended for patients with symptoms &gt;3 months or receiving treatment with gentamicin plus vancomycin.",
"     <br/>",
"     &sect; Vancomycin therapy only recommended for patients allergic to penicillin and cephalosporins; vancomycin dose adjusted for peak serum concentrations (one hour after infusion) of 30 to 45 mcg/mL and trough concentration of 10 to 15 mcg/mL.",
"     <br/>",
"     &yen; Alternative for penicillin-allergic patient provided isolate is teicoplanin susceptible (MIC &le;4 mg/L).",
"    </div>",
"    <div class=\"reference\">",
"     Circulation 2005; 111:3167; J Antimicrob Chemother 2012; 67:269; Eur Heart J 2009; 30:2369.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
