" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Primary dysmenorrhea in adolescents",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?30/25/31125/contributors\">",
