var title_f30_25_31131="Clinician tasks";
var content_f30_25_31131=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F51924&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F51924&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    The clinician's tasks during a patient visit",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Get the visit off to a good start",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Learn what the patient hopes to accomplish in the visit (the patient's agenda)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Elicit the patient's complaints (symptoms), concerns, and requests",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Perform an appropriately focused physical examination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Mentally test diagnostic hypotheses throughout the collection of historical and examination data",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Develop a differential diagnosis of the problem(s)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Plan a cost-effective work-up if needed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Choose a therapeutic intervention if needed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Discuss all of this with the patient in terms that are easily understood",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Check the patient's understanding",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Negotiate differences of opinion the patient and doctor may have about the nature of the problem, its evaluation, and its treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Assure that attention is given to disease prevention strategies appropriate to the patient's age, gender, habits, genetic and socioeconomic risk factors",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Make plans for follow-up of the problem and its treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Conclude the visit",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
