var title_f30_29_31196="Treatment of hypercalcemia";
var content_f30_29_31196=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F73030&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F73030&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Treatment of hypercalcemia",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"20%\">",
"     </colgroup>",
"     <colgroup width=\"40%\">",
"     </colgroup>",
"     <colgroup span=\"2\" width=\"20%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Intervention",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mode of action",
"       </td>",
"       <td class=\"subtitle1\">",
"        Onset of action",
"       </td>",
"       <td class=\"subtitle1\">",
"        Duration of action",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Isotonic saline hydration",
"       </td>",
"       <td>",
"        <p>",
"         Restoration of intravascular volume",
"        </p>",
"        Increases urinary calcium excretion",
"       </td>",
"       <td>",
"        Hours",
"       </td>",
"       <td>",
"        During infusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Calcitonin",
"       </td>",
"       <td>",
"        <p>",
"         Inhibits bone resorption via interference with osteoclast function",
"        </p>",
"        Promotes urinary calcium excretion",
"       </td>",
"       <td>",
"        4 to 6 hours",
"       </td>",
"       <td>",
"        48 hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bisphosphonates",
"       </td>",
"       <td>",
"        Inhibit bone resorption via interference with osteoclast recruitment and function",
"       </td>",
"       <td>",
"        24 to 72 hours",
"       </td>",
"       <td>",
"        2 to 4 weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Loop diuretics*",
"       </td>",
"       <td>",
"        Increase urinary calcium excretion via inhibition of calcium reabsorption in the loop of Henle",
"       </td>",
"       <td>",
"        Hours",
"       </td>",
"       <td>",
"        During therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Glucocorticoids",
"       </td>",
"       <td>",
"        <p>",
"         Decrease intestinal calcium absorption",
"        </p>",
"        Decrease 1,25-dihydroxyvitamin D production by activated mononuclear cells in patients with granulomatous diseases or lymphoma",
"       </td>",
"       <td>",
"        2 to 5 days",
"       </td>",
"       <td>",
"        Days to weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Gallium nitrate",
"       </td>",
"       <td>",
"        Inhibits osteoclast-mediated bone resorption",
"       </td>",
"       <td>",
"        3 to 5 days",
"       </td>",
"       <td>",
"        2 weeks",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Calcimimetics",
"       </td>",
"       <td>",
"        Calcium sensing receptor agonist, reduces PTH (parathyroid carcinoma, secondary hyperparathyroidism in CKD)",
"       </td>",
"       <td>",
"        2 to 3 days",
"       </td>",
"       <td>",
"        During therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dialysis",
"       </td>",
"       <td>",
"        Low or no calcium dialysate",
"       </td>",
"       <td>",
"        Hours",
"       </td>",
"       <td>",
"        During treatment",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Loop diuretics should not be used routinely. However, in patients with renal insufficiency or heart failure, judicious use of loop diuretics may be required to prevent fluid overload during saline hydration.",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Shane E, Dinaz I. Hypercalcemia: pathogenesis, clinical manifestations, differential diagnosis, and management. In: Primer on the Metabolic Bone Diseases and Disorders of Mineral Metabolism (Sixth Edition). American Society of Bone and Mineral Research 2006; 179.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
