" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Hereditary hemorrhagic telangiectasia (Osler-Weber-Rendu syndrome)",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?30/31/31226/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?30/31/31226/contributors\">",
"     Claire Shovlin, PhD, FRCP",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?30/31/31226/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?30/31/31226/contributors\">",
"     Lawrence LK Leung, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?30/31/31226/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
