" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Principles of labor induction",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?30/37/31322/contributors\">",
