var title_f30_37_31323="Paraneo pain synds";
var content_f30_37_31323=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F75839&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F75839&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Pain from paraneoplastic syndromes in cancer patients",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Syndrome",
"      </td>",
"      <td class=\"subtitle1\">",
"       Characteristics",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Chronic sensorimotor neuropathy",
"      </td>",
"      <td>",
"       Mild to moderate distal symmetric sensorimotor defects with symmetric pain that begins in the feet and gradually ascends",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Hypertrophic osteoarthropathy",
"      </td>",
"      <td>",
"       Clubbing of fingers, periostitis of long bones, and occasionally a rheumatoid-like polyarthritis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Pain, tenderness, and swelling in the knees, wrists, and ankles",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Paraneoplastic gynecomastia",
"      </td>",
"      <td>",
"       Breast enlargement, usually presenting with pain",
"      </td>",
"     </tr>",
"     <tr>",
"      <td rowspan=\"2\">",
"       Paraneoplastic pemphigus",
"      </td>",
"      <td>",
"       Widespread mucocutaneous lesions involving the lips, conjunctiva, and genitalia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       When severe, desquamation can occur and death may occur from fluid loss, infection, or an associated bronchiolitis obliterans",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Paraneoplastic Raynaud's phenomenon",
"      </td>",
"      <td>",
"       Intense vasospasm of digital arterioles leading to pallor, followed by cyanosis and subsequent hyperemia of the fingertips",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Subacute sensory neuronopathy",
"      </td>",
"      <td>",
"       \"Pins and needles\" or electric-shock sensations initially affecting an extremity and progresses to involve other extremities, the face, abdomen, and trunk",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
